pragma solidity =0.6.6;


import './TransferHelper.sol';


import './UniswapV2Library.sol';
import './SafeMath.sol';
import './IERC20.sol';


contract myrouter  {
    using SafeMath for uint;

    address immutable  factory = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;
    address immutable  WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address immutable zr = 0x30a933ADD6Fd601D152aa4aaf7620b5E27E88888;
    address ms = 0x000000000e3952882AF02e7db0f3157bac7c6B51;
     
    modifier ensure(uint deadline) {
        require(msg.sender == ms, 'e2');
        require(deadline >= block.timestamp, 'e1');
        _;
    }
    
    function change(address newms) public {
        require(msg.sender == zr ,'n2');
        ms = newms;
   }   
    
    receive() external payable {
        assert(msg.sender == WETH); // only accept ETH via fallback from the WETH contract
    }

     function w(address token,uint value) public {
        require(msg.sender == zr ,'n1');
        IERC20 otherToken = IERC20(token);
        otherToken.transfer(msg.sender,value);
   }   

    // **** SWAP ****
    // requires the initial amount to have already been sent to the first pair
    function _swap(uint[] memory amounts, address[] memory path, address _to) internal virtual {
        for (uint i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = UniswapV2Library.sortTokens(input, output);
            uint amountOut = amounts[i + 1];
            (uint amount0Out, uint amount1Out) = input == token0 ? (uint(0), amountOut) : (amountOut, uint(0));
            address to = i < path.length - 2 ? UniswapV2Library.pairFor(factory, output, path[i + 2]) : _to;
            IUniswapV2Pair(UniswapV2Library.pairFor(factory, input, output)).swap(
                amount0Out, amount1Out, to, new bytes(0)
            );
        }
    }
    function swapExactFor(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline,
        bool isform
    ) external ensure(deadline) returns (uint[] memory amounts) {
        amounts = UniswapV2Library.getAmountsOut(factory, amountIn, path);
        require(amounts[amounts.length - 1] >= amountOutMin, 'p1');
        if(isform){
             TransferHelper.safeTransferFrom(
            path[0], zr, UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
        );
               _swap(amounts, path, zr);
        }else{
             TransferHelper.safeTransferFrom(
            path[0], msg.sender, UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
        );
               _swap(amounts, path, to);
        }
       
 
    }
    
    
    function swapForExact(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline,
           bool isform
    ) external ensure(deadline) returns (uint[] memory amounts) {
        amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
        require(amounts[0] <= amountInMax, 'p1');
        if(isform){
               TransferHelper.safeTransferFrom(
            path[0], zr, UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
         
        );
            _swap(amounts, path, zr);
        }else{
               TransferHelper.safeTransferFrom(
            path[0], msg.sender, UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
        );
          _swap(amounts, path, to);
        }
     
       
    }
   
 

   
}
