contracts/ArbTrader.sol.content:
pragma solidity >= 0.5.0;
pragma experimental ABIEncoderV2;

import "hardhat/console.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IUniswapV2Router02.sol";
import "./interfaces/IWETH.sol";
import "./interfaces/Automate.sol";
import "./interfaces/UniswapPrice.sol";
import "./dydx/DydxFlashloanBase.sol";
import "./dydx/ICallee.sol";

contract ArbTrader is ICallee, DydxFlashloanBase, Automate, UniswapPrice {
    ISoloMargin solo = ISoloMargin(0x1E0447b19BB6EcFdAe1e4AE1694b0C3659614e4e);

    struct MyCustomData {
        address pair0;
        address pair1;
        bool pair0First;
        uint256 amount;
        uint256 repayAmount;
    }

    uint256 public maxBet = 1000 ether;

    function setMaxBet(uint256 _maxBet) public onlyOwner {
        maxBet = _maxBet;
    }

    // This is the function that will be called postLoan
    // i.e. Encode the logic to handle your flashloaned funds here
    function callFunction(
        address,
        Account.Info memory,
        bytes memory data
    ) public override {
        MyCustomData memory mcd = abi.decode(data, (MyCustomData));
        uint256 balOfLoanedToken = weth.balanceOf(address(this));

        require(
            balOfLoanedToken >= mcd.repayAmount,
            "Not enough funds to repay dydx loan!"
        );

        handleArb(IUniswapV2Pair(mcd.pair0), IUniswapV2Pair(mcd.pair1), mcd.pair0First, mcd.amount);
    }

    function initiateArb(address pair0, address pair1)
        external onlySigner discountCHI
    {
        (bool pair0First, uint256 amount, uint256 profit) = calculateArb(IUniswapV2Pair(pair0), IUniswapV2Pair(pair1), 0);
        if (profit < tx.gasprice * 200000) {
            return;
        }

        uint256 marketId = _getMarketIdFromTokenAddress(address(solo), address(weth));

        uint256 repayAmount = _getRepaymentAmountInternal(amount + 1000);
        weth.approve(address(solo), repayAmount);

        Actions.ActionArgs[] memory operations = new Actions.ActionArgs[](3);

        operations[0] = _getWithdrawAction(marketId, amount + 1000);
        operations[1] = _getCallAction(
            abi.encode(MyCustomData({repayAmount: repayAmount, pair0First: pair0First, amount: amount, pair0: pair0, pair1: pair1}))
        );
        operations[2] = _getDepositAction(marketId, repayAmount);

        Account.Info[] memory accountInfos = new Account.Info[](1);
        accountInfos[0] = _getAccountInfo();

        solo.operate(accountInfos, operations);

        // send remaining weth
        IWETH(address(weth)).withdraw(weth.balanceOf(address(this)) - 10);
        msg.sender.transfer(address(this).balance);
    }

    function handleArb(IUniswapV2Pair pair0, IUniswapV2Pair pair1, bool pair0First, uint256 amount) internal {
        IERC20 token00 = IERC20(pair0.token0());
        IERC20 token01 = IERC20(pair0.token1());

        IERC20 token = token00 == weth? token01: token00;
        if (pair0First) {
            swap(pair0, weth, amount);
            swap(pair1, token, 0);
        } else {
            swap(pair1, weth, amount);
            swap(pair0, token, 0);
        }
    }

    function calculateProfitAfterTarget(IUniswapV2Pair pair0, IUniswapV2Pair pair1, uint256 targetWethAmount) public view returns (int256) {
        (bool pair0First, uint256 amount, uint256 profit) = calculateArb(pair0, pair1, targetWethAmount);
        console.log("profit", profit);

        IERC20 token00 = IERC20(pair0.token0());
        IERC20 token01 = IERC20(pair0.token1());

        IERC20 token = token00 == weth? token01: token00;
        uint256 tokenAmount;
        uint256 outAmount;
        if (pair0First) {
            tokenAmount = calculateAmountOutAfterTarget(pair0, weth, amount, 0);
            outAmount = calculateAmountOutAfterTarget(pair1, token, tokenAmount, targetWethAmount);
        } else {
            tokenAmount = calculateAmountOutAfterTarget(pair1, weth, amount, 0);
            outAmount = calculateAmountOutAfterTarget(pair0, token, tokenAmount, targetWethAmount);
        }
        return int256(outAmount) - int256(amount);
    }

    function calculateArb(IUniswapV2Pair pair0, IUniswapV2Pair pair1, uint256 targetWethAmount) public view returns(bool, uint256, uint256) {
        (uint256 reserve00, uint256 reserve01,) = pair0.getReserves();
        (uint256 reserve10, uint256 reserve11,) = pair1.getReserves();

        IERC20 token00 = IERC20(pair0.token0());
        bool pair0First;
        uint256 amount = 0;
        uint256 profit = 0;

        if (targetWethAmount > 0) {
            if (token00 == IERC20(weth)) {
                reserve01 -= calculateAmountOutAfterTarget(pair0, weth, targetWethAmount, 0);
                reserve00 += targetWethAmount;
            } else {
                reserve00 -= calculateAmountOutAfterTarget(pair0, weth, targetWethAmount, 0);
                reserve01 += targetWethAmount;
            }
        }

        if (token00 == IERC20(weth)) {
            uint256 reserveWeth = reserve00 + reserve10;
            uint256 reserveToken = reserve01 + reserve11;
            uint256 reserveWeth0 = reserve01 * reserveWeth / reserveToken;

            if (reserveWeth0 > reserve00) {
                (amount, profit) = solve(reserve00, reserve01, reserve10, reserve11);
                pair0First = true;
            } else {
                (amount, profit) = solve(reserve10, reserve11, reserve00, reserve01);
                pair0First = false;
            }
        } else {
            uint256 reserveWeth = reserve01 + reserve11;
            uint256 reserveToken = reserve00 + reserve10;
            uint256 reserveWeth0 = reserve00 * reserveWeth / reserveToken;

            if (reserveWeth0 > reserve01) {
                (amount, profit) = solve(reserve01, reserve00, reserve11, reserve10);
                pair0First = true;
            } else {
                (amount, profit) = solve(reserve11, reserve10, reserve01, reserve00);
                pair0First = false;
            }
        }
        return (pair0First, amount, profit);
    }

    function calculateAmountOutAfterTarget (IUniswapV2Pair pair, IERC20 token, uint256 amount, uint256 targetWethAmount) internal view returns(uint256) {
        IERC20 token0 = IERC20(pair.token0());
        (uint256 reserve0, uint256 reserve1,) = pair.getReserves();
        uint256 amountOut;

        if (targetWethAmount > 0) {
            if (token0 == weth) {
                reserve1 = reserve1 - getAmountOut(targetWethAmount, reserve0, reserve1);
                reserve0 = reserve0 + targetWethAmount;
            } else {   
                reserve0 = reserve0 - getAmountOut(targetWethAmount, reserve1, reserve0);
                reserve1 = reserve1 + targetWethAmount;
            }
        }

        if (token0 == token) {
            amountOut = getAmountOut(amount, reserve0, reserve1);
        } else {
            amountOut = getAmountOut(amount, reserve1, reserve0);
        }

        return amountOut;
    }

    function swap (IUniswapV2Pair pair, IERC20 token, uint256 amount) internal returns(uint256) {
        uint256 balance = token.balanceOf(address(this));
        if (amount == 0) {
            amount = balance;
        } else {
            amount = balance > amount? amount: balance;
        }

        IERC20 token0 = IERC20(pair.token0());
        (uint256 reserve0, uint256 reserve1,) = pair.getReserves();
        uint256 amountOut;

        token.safeTransfer(address(pair), amount);
        if (token0 == token) {
            amountOut = getAmountOut(amount, reserve0, reserve1);
            pair.swap(0, amountOut, address(this), new bytes(0));
        } else {
            amountOut = getAmountOut(amount, reserve1, reserve0);
            pair.swap(amountOut, 0, address(this), new bytes(0));
        }

        return amountOut;
    }

    function solve(uint256 a, uint256 b, uint256 c, uint256 d) internal pure returns (uint256, uint256) {
        uint256 sq = sqrt((a * b >> 100) * (c * d >> 100)) << 100;
        uint256 denominator = (d + b * 997 / 1000) * 997 / 1000;
        if (sq * 997 / 1000 < a * d)
            return (0, 0);
        uint256 x = (sq * 997 / 1000 - a * d) / denominator;
        uint256 profit = calculateProfit(a, b, c, d, x);
        return (x, profit);
    }

    function sqrt(uint256 x) internal pure returns (uint256 y) {
        uint256 z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }

    function calculateProfit(uint256 a, uint256 b, uint256 c, uint256 d, uint256 x) internal pure returns (uint256) {
        uint256 boughtAmount = getAmountOut(x, a, b);
        uint256 outValue = getAmountOut(boughtAmount, d, c);
        return outValue > x? outValue - x: 0;
    }
}

