//SPDX-License-Identifier: Unlicense
pragma solidity >= 0.5.0;

import "./IUniswapV2Router02.sol";
import "./IUniswapV2Pair.sol";
import "./IWETH.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

interface ICHI {
  function freeUpTo(uint256 value) external returns (uint256);
}

contract Automate {
  using SafeMath for uint256;
  using SafeERC20 for IERC20;

  ICHI chi = ICHI(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c);
  IUniswapV2Router02 private router02 = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
  IERC20 weth = IERC20(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);

  address payable owner = 0xd6Fe434AfD8A03CF2ad770Cc4E0403b262168B0c;

  mapping(address => bool) public isSigner;

  constructor () public {
    isSigner[owner] = true; // Owner
  }

  receive() external payable { }

  modifier onlyOwner {
    require(msg.sender == owner);
    _;
  }

  modifier onlySigner {
    require(isSigner[msg.sender], "InvalidS");
    _;
  }

  function destruct () public onlyOwner {
    selfdestruct(owner);
  }

  modifier discountCHI {
    uint256 gasStart = gasleft();
    _;
    uint256 gasSpent = 21000 + gasStart - gasleft() + 16 *
                       msg.data.length;
    chi.freeUpTo((gasSpent + 14154) / 41947);
  }

  function recoverETH () public onlyOwner {
      owner.transfer(address(this).balance);
  }

  function recoverERC20 (IERC20 token) public onlyOwner {
    token.safeTransfer(owner, token.balanceOf(address(this)));
  }

  function recoverERC20ByAmount (IERC20 token, uint256 amount) public onlyOwner {
    token.safeTransfer(owner, amount);
  }

  function setOwner (address payable newOwner) public onlyOwner {
    owner = newOwner;
  }

  function addSigner (address signer) public onlyOwner {
    isSigner[signer] = true;
  }

  function revokeSigner (address signer) public onlyOwner {
    isSigner[signer] = false;
  }
}

