pragma solidity >=0.4.22 <0.7.0;

contract SimpleGrafiti {
    address owner; 
    string grafiti;

    constructor() public{
        owner = msg.sender;
    }

    modifier onlyOwner () {
        require(msg.sender == owner);
        _;
    }

    function tag( string memory str) public onlyOwner {
        grafiti = str;
    }

    function read() public view returns (string memory){
        return grafiti;
    }
}
