// SPDX-License-Identifier: Unlicensed

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract SimpleDao is Ownable, ERC20Burnable {
    using ECDSA for *;

    uint256 constant MAX_INT = 2**256 - 1;

    // TODO: think through this and how flexible we want it a bit more
    uint256 tokenRate = 10**14;

    constructor(
        string memory _name,
        string memory _symbol,
        uint256 _initialRate
    ) ERC20(_name, _symbol) {
        tokenRate = _initialRate;
    }

    /////////////////////////
    //  Events
    /////////////////////////

    /// @notice Emitted on every deposit.
    event Deposit(address member, uint256 amount);

    /// @notice Emitted on ever withdrawal.
    event Withdrawal(address member, uint256 amount);

    /////////////////////////
    //  Functions + Hooks
    /////////////////////////

    function _maxDeposit(address) internal virtual returns (uint256) {
        return MAX_INT;
    }

    /// @notice The max amount of tokens a person is allowed to withdraw at this time.
    /// @dev If not overridden, this function will allow DAO members to withdraw fully at any time.
    function _maxBurn(address _member) internal virtual returns (uint256) {
        return this.balanceOf(_member);
    }

    /// @dev Hook called after each successful deposit
    function _onDeposit(address _member, uint256 _amount) internal virtual {}

    /// @dev Hook called after each successful withdrawal
    function _onWithdraw(address _member, uint256 _amount) internal virtual {}

    function deposit() external payable {
        uint256 maxDeposit = _maxDeposit(msg.sender);
        require(maxDeposit > 0, "deposit rejected");

        uint256 amt = msg.value;
        uint256 excess = 0;
        if (amt > maxDeposit) {
            excess = amt - maxDeposit;
            amt = maxDeposit;
        }

        _mint(msg.sender, amt / tokenRate);

        // return excess funds
        // TODO: it'd be nice if we returned anything past the token rate,
        // or we could maybe keep the slippage for ourselves... seems a little
        // dirty though
        (bool sent, ) = msg.sender.call{value: excess}("");
        require(sent, "failed to return excess Ether");

        _onDeposit(msg.sender, msg.value);
        emit Deposit(msg.sender, msg.value);
    }

    /// @notice Burns a member's tokens in exchange for Ether at the current exchange rate.
    function burn(uint256 _tokenAmt) public override {
        uint256 maxWithdrawal = _maxBurn(msg.sender);
        require(maxWithdrawal > 0, "withdrawal rejected, no tokens to withdraw");

        if (_tokenAmt > maxWithdrawal) {
            _tokenAmt = maxWithdrawal;
        }

        // guard against reentrancy by burning tokens first
        _burn(msg.sender, _tokenAmt);

        // send ether at current token exchange rate
        (bool sent, ) = msg.sender.call{value: _tokenAmt * tokenRate}("");
        require(sent, "failed to send Ether");

        _onWithdraw(msg.sender, _tokenAmt * tokenRate);
        emit Withdrawal(msg.sender, _tokenAmt * tokenRate);
    }

    /////////////////////////
    //  Admin (BlockParties)
    /////////////////////////

    function setTokenRate(uint256 _rate) public {
        Ownable daoFactory = Ownable(owner());
        require(daoFactory.owner() == msg.sender, "only admin is authorized to manually change state");
        tokenRate = _rate;
    }

    /// @notice Executes a meta-transaction on behalf of the DAO.
    ///         Requires that the transaction have been authorized
    ///         by Koop.
    function authorizedProxyCall(
        address _address,
        bytes calldata _data,
        uint256 _value,
        bytes32 preSignedHash,
        bytes calldata _signature
    ) external returns (address) {
        Ownable daoFactory = Ownable(owner());
        address signer = preSignedHash.recover(_signature);

        require(signer == daoFactory.owner(), "Unauthorized! Signatures don't match.");

        assembly {
            let ptr := mload(0x40)
            calldatacopy(ptr, _data.offset, _data.length)

            let result := call(gas(), _address, _value, ptr, _data.length, 0, 0)
            let size := returndatasize()
            returndatacopy(ptr, 0, size)

            switch result
            case 0 {
                revert(ptr, size)
            }
            default {
                return(ptr, size)
            }
        }
    }

    /////////////////////////
    //  Getters
    /////////////////////////

    function getTokenRate() external view returns (uint256) {
        return tokenRate;
    }
}

