// SPDX-License-Identifier: Unlicensed

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "./SimpleDao.sol";

interface ERC721 is IERC721 {

}

interface ERC1155 is IERC1155 {

}

contract SingleNftDao is SimpleDao, IERC721Receiver, IERC1155Receiver {
    struct NFT {
        address tokenAddress;
        uint256 tokenId;
        AssetType assetType;
    }

    enum State {
        CROWDFUNDING,
        FUNDED,
        HOLDING,
        LISTED,
        SOLD
    }

    enum AssetType {
        ERC721,
        ERC1155
    }

    /////////////////////////
    //  Variables
    /////////////////////////

    /// @notice The NFT meant to be acquired by the party.
    NFT nft;

    /// @notice The party's state.
    State state = State.CROWDFUNDING;
    AssetType assetType;

    /// @notice The crowdfunding target, presumably exactly the price of the NFT.
    uint256 targetPrice;

    /////////////////////////
    //  State Modifying Func.
    /////////////////////////

    constructor(
        address _nftAddress,
        uint256 _nftTokenId,
        AssetType _assetType,
        uint256 _targetPrice,
        string memory _name,
        string memory _symbol,
        uint256 _initialRate
    ) SimpleDao(_name, _symbol, _initialRate) {
        nft.tokenAddress = _nftAddress;
        nft.tokenId = _nftTokenId;
        nft.assetType = _assetType;
        targetPrice = _targetPrice;
    }

    function _maxDeposit(address) internal override returns (uint256) {
        require(state == State.CROWDFUNDING, "party was already funded");
        return targetPrice - (address(this).balance - msg.value);
    }

    function _daoIsOwner() internal view returns (bool) {
        if(nft.assetType == AssetType.ERC721) {
            return ERC721(nft.tokenAddress).ownerOf(nft.tokenId) == address(this);
        } else if (nft.assetType == AssetType.ERC1155) {
            return ERC1155(nft.tokenAddress).balanceOf(address(this), nft.tokenId) > 0;
        } else {
            revert("unexpected asset type specified");
        }
    }

    function _maxBurn(address _member) internal override returns (uint256) {
        // check if NFT was just sold
        if (state == State.HOLDING && address(this).balance > 0 && !_daoIsOwner()) {
            state = State.SOLD;
            tokenRate = address(this).balance / totalSupply();
        }

        return this.balanceOf(_member);
    }

    function _onDeposit(address _member, uint256 _amount) internal override {
        if (address(this).balance == targetPrice) {
            state = State.FUNDED;
        }
        super._onDeposit(_member, _amount);
    }

    function _onWithdraw(address _member, uint256 _amount) internal override {
        if (state == State.FUNDED && address(this).balance != targetPrice) {
            state = State.CROWDFUNDING;
        }
        super._onWithdraw(_member, _amount);
    }

    /////////////////////////
    //  Getters
    /////////////////////////

    function getNft() external view returns (address, uint256) {
        return (nft.tokenAddress, nft.tokenId);
    }

    function getState() external view returns (State) {
        return state;
    }

    /// @notice Return the percentage raised towards the target goal, with 2 decimal places.
    ///         E.g. 50% is returned as 5000
    function getPercentFunded() external view returns (uint256) {
        return (10000 * address(this).balance) / targetPrice;
    }

    function daoIsOwner() external view returns (bool) {
        return _daoIsOwner();
    }

    /////////////////////////
    //  Event Hooks
    /////////////////////////

    function onERC721Received(
        address _operator,
        address _from,
        uint256 _tokenId,
        bytes calldata
    ) external override returns (bytes4) {
        // update state only if dao has received the specified NFT 
        if(msg.sender == nft.tokenAddress && _tokenId == nft.tokenId) {
            state = State.HOLDING;
        }

        return
            bytes4(
                keccak256("onERC721Received(address,address,uint256,bytes)")
            );
    }

    function onERC1155Received(
        address _operator,
        address _from,
        uint256 _id,
        uint256 _value,
        bytes calldata
    ) external override returns (bytes4) {
        if(msg.sender == nft.tokenAddress && _id == nft.tokenId) {
            state = State.HOLDING;
        }

        return
            bytes4(
                keccak256(
                    "onERC1155Received(address,address,uint256,uint256,bytes)"
                )
            );
    }

    function onERC1155BatchReceived(
        address operator,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata
    ) external override returns (bytes4) {
        if(msg.sender == nft.tokenAddress) {
            for(uint i = 0; i < ids.length; i++) {
                if(ids[i] == nft.tokenId) {
                    state = State.HOLDING;
                }                
            }
        }

        return
            bytes4(
                keccak256(
                    "onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"
                )
            );
    }

    function supportsInterface(bytes4 interfaceId)
        external
        view
        override
        returns (bool)
    {
        return true;
    }

    function setState(State _state) public {
        Ownable daoFactory = Ownable(owner());
        require(daoFactory.owner() == msg.sender, "only admin is authorized to manually change state");
        state = _state;
    }
}

