pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./SingleNftDao.sol";

contract SingleNftDaoFactory is Ownable {
    event DaoCreated(address host, address deployedAddress, bool isPrivate);

    function deploy(
        bool _isPrivate,
        address _nftAddress,
        uint256 _nftTokenId,
        SingleNftDao.AssetType _assetType,
        uint256 _targetPrice,
        string memory _name,
        string memory _symbol,
        uint256 _initialRate
    ) external {
        SingleNftDao dao = new SingleNftDao(
            _nftAddress,
            _nftTokenId,
            _assetType,
            _targetPrice,
            _name,
            _symbol,
            _initialRate
        );

        emit DaoCreated(msg.sender, address(dao), _isPrivate);
    }
}
