// SPDX-License-Identifier: MIT

pragma solidity ^0.8.2;

import "./SuikaFlattened.sol";

/**
 *
 * WPSmartContracts / Tea Contract
 *
 * Charge the fee in Tokens
 *
 */
contract Tea {

    // WPST address
    EIP20 public token;

    // ratios
    uint256 rewardsRatio;
    uint256 claimRatio;

    // the master address to verify ownership of the user
    address public master;

    bool public enableTea;

    // the Token address of the owner
    address public masterAddress;

    // saves the attributes for Tea
    constructor(address _master, EIP20 _token, uint256 _rewardsRatio, uint256 _claimRatio, address _masterAddress) {

        // set ratios for payments
        rewardsRatio = _rewardsRatio;
        claimRatio = _claimRatio;

        // set the manager of the contract
        master = _master;

        // set token address
        token = _token;

        // set master address and fee for payments
        masterAddress = _masterAddress;

        enableTea = false;

    }

    // restrict methods to the master
    modifier onlyMaster() {
        require(msg.sender == master, "Only master can do this");
        _;
    }

    // get rewards gained when paying in_wei weis
    function getRewards(uint256 in_wei) public view returns (uint256) {
        return in_wei * rewardsRatio;
    }

    // get price in tokens to claim a reward using tokens
    function getPriceInToken(uint256 in_wei) public view returns (uint256) {
        return in_wei * claimRatio;
    }

    // update token address
    // restricted to master
    function updateToken(EIP20 _token) public onlyMaster {
        // set token address
        token = _token;
    }

    // enable - disable tea
    function updateEnabled(bool _enabled) public onlyMaster {
        enableTea = _enabled;
    }

    // set a new ratio for an specific contract type
    // restricted to master
    function updateRewardsRatio(uint256 _rewardsRatio) public onlyMaster {
        // set ratio
        rewardsRatio = _rewardsRatio;
    }

    // set a new ratio for an specific contract type
    // restricted to master
    function updateClaimRatio(uint256 _claimRatio) public onlyMaster {
        // set ratio
        claimRatio = _claimRatio;
    }

    // pay the tip with tokens (in wei)
    function buyTea(uint256 in_wei, address from_address) public {
        require(enableTea, "WPIC deployments are not enabled now");
        // Buy me a tea
        token.transferFrom(from_address, masterAddress, getPriceInToken(in_wei));
    }

    // check if an account can pay the fee in WPST
    function canBuyTea(address account, uint256 in_wei) public view returns (bool) {
        if (token.allowance(account, address(this)) >= in_wei) {
            return true;
        } else {
            return false;
        }
    }

    // try to give away tokens when users buy coffee
    function tryToGiveAwayWPST(uint256 masterFee) internal {
        if (address(token)!=address(0)) {
            // get rewards
            uint256 rewards = getRewards(masterFee);
            // the factory needs to have enough tokens approved to give away
            if (token.allowance(masterAddress, address(this)) >= rewards) {
                // give away
                token.transferFrom(masterAddress, msg.sender, rewards);
            }
        }
    }

    // update address of the master for new contracts
    // restricted to master
    function updateMasterAddress(address payable _masterAddress) public onlyMaster {
        // set master payment address
        masterAddress = _masterAddress;
    }

}

/**
 *
 * WPSmartContracts / Coffee Contract
 *
 * Charge the fee and keep track of deployed contracts
 *
 */

contract Coffee is Tea {

    // reentrancy guard
    bool locked = false;

    // deploy fee (in wei)
    uint256 public masterFee;

    // saves the master address (from sender) and for payments    
    constructor(address _master, EIP20 _token, uint256 _rewardsRatio, uint256 _claimRatio, uint256 _masterFee, address payable _masterAddress) 
        Tea(_master, _token, _rewardsRatio, _claimRatio, _masterAddress) {

        // Check for valid value
        require(_masterFee > 0, "Coffee fee has to be positive");

        // set fee for payments
        masterFee = _masterFee;

    }

    // set a new fee for an specific contract type
    // restricted to master
    function updateFee(uint256 _masterFee) public onlyMaster {
        // set master parameters
        masterFee = _masterFee;
    }

    // pay the tip
    function buyCoffee() public payable {

        require(!locked, "Reentrant call detected!");
        locked = true;

        // Is a fee defined for this type_
        // Can the sender pay the deploy?
        require(msg.value >= masterFee, "Not enough funds");

        // Buy me a coffe
        (bool success, ) = masterAddress.call{value: msg.value}("");
        require(success, "Transfer failed.");

        locked = false;

    }

}

contract SuikaFactory is Coffee {

    event newContract(address add);

    // defines master address, call Coffee parameters: master and address fee
    constructor(EIP20 _token, uint256 _rewardsRatio, uint256 _claimRatio, uint256 _masterFee, address payable _masterAddress)
        Coffee(msg.sender, _token, _rewardsRatio, _claimRatio, _masterFee, _masterAddress) {}

    // create one Token with: symbol, name and total supply, cap is maximum capitalization (optional, 0 unlimited)

    function createContract(EIP20 _payment_token, address payable _admin, uint256 _commissionRate, uint256 _royaltiesCommissionRate, string memory name, string memory symbol, bool _anyoneCanMint) public payable {

        // pay the fee with ether
        buyCoffee();

        // try to give away tokens when users buy coffee
        tryToGiveAwayWPST(masterFee);

        // call the real create token function
        realCreateContract(_payment_token, _admin, _commissionRate, _royaltiesCommissionRate, name, symbol, _anyoneCanMint);

    }

    // create one Token with WPST with: symbol, name and total supply
    function createContractWithWPST(EIP20 _payment_token, address payable _admin, uint256 _commissionRate, uint256 _royaltiesCommissionRate, string memory name, string memory symbol, bool _anyoneCanMint) public {

        // pay the fee with tokens
        buyTea(masterFee, msg.sender);

        // call the real create token function
        realCreateContract(_payment_token, _admin, _commissionRate, _royaltiesCommissionRate, name, symbol, _anyoneCanMint);

    }

    function realCreateContract(EIP20 _payment_token, address payable _admin, uint256 _commissionRate, uint256 _royaltiesCommissionRate, string memory name, string memory symbol, bool _anyoneCanMint) internal {

        // deploys the contract and store the address
        ERC721Suika the_contract = new ERC721Suika(_payment_token, msg.sender, _admin, _commissionRate, _royaltiesCommissionRate, name, symbol, _anyoneCanMint);

        emit newContract(address(the_contract));

    }

}

