// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.0;

import "./libraries/SafeMath.sol";
import "./interfaces/IHasher.sol";

contract MaskVote {

    using SafeMath for uint;

    address HASHER;

    constructor(address hasher_) {
        HASHER = hasher_;
    } 

    function verifyTrueAppraisal(
        bytes32 concealedBid, 
        uint a, 
        uint b, 
        uint c, 
        uint d, 
        uint e,
        address _user
    ) view external returns (bool) {
        uint[5] memory valueList = [a,b,c,d,e];
        for(uint i = 0; i < 5; i++) {
            if(concealedBid == keccak256(abi.encodePacked(valueList[i], _user, IHasher(HASHER).getUserRandomValue(_user)))) {
                return true;
            }
        }
        return false;
    }

    /// @notice Verify that user appraisal is within limit of 2.5x max appraisal.
    function verifyWithinLimit(
        uint _maxAppraisal, 
        uint a, 
        uint b, 
        uint c, 
        uint d, 
        uint e
    ) pure external returns (bool){
        uint[5] memory valueList = [a,b,c,d,e];
        for(uint x = 0; x < valueList.length; x++) {
            if (valueList[x] > _maxAppraisal.mul(25).div(10)) {
                return false;
            }
        }
        return true;
    }
}

