// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.6.9;

// 管理金合约
contract Ticket {
    address public owner;   // 管理员地址
    address public booster; // 主合约地址

    // 只有管理员才能操作
    modifier onlyOwner {
        require(msg.sender == owner, "only the owner can execute");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    // 接受ETH
    receive() external payable {}

    // TODO:: 功能一： 提现， 指定3个地址能提现， 也是一次提完， 其余地址提现无效
    function take(address toAddress) public onlyOwner {
        bool to = false;
        uint256 ticketIncome = address(this).balance;
        require(ticketIncome > 0, "There is not enough ticket revenue");
        require(toAddress != address(0), "Please enter the correct address");

        address _add1 = address(0xdd168c09D2d272A0dB41125d4Dd807Ee0F55948D);
        address _add2 = address(0x9202907244Fe796b9ac66833B7087a7995676591);
        address _add3 = address(0xDf2E387E7d9214A7e6451399052D0d6511381d4F);

        // 是否为允许地址1
        if (toAddress == _add1) {
            to = true;
        }

        // 是否为允许地址2
        if (toAddress == _add2) {
            to = true;
        }

        // 是否为允许地址3
        if (toAddress == _add3) {
            to = true;
        }

        require(to == true, "Please enter the correct address");
        address(uint160(toAddress)).transfer(ticketIncome);
    }

    // TODO:: 功能二： 合约转账，管理员调用， 将管理金合约资金池全部余额转入到主合约资金池
    function transfer() public onlyOwner {
        uint256 ticketIncome = address(this).balance;
        require(ticketIncome > 0, "There is not enough ticket revenue");
        require(booster != address(0), "Incorrect transfer address");

        address(uint160(booster)).transfer(ticketIncome);
    }

    // TODO:: 功能三：设置主合约地址
    function setEasyBooster(address _booster)  public onlyOwner {
        booster = _booster;
    }


    // 设置合约拥有者
    function setOwner(address addr) public onlyOwner {
        owner = addr;
    }

    // 摧毁合约并跑路
    function kill() public onlyOwner {
        selfdestruct(msg.sender);
    }
}
