// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.6.0;

import { Chi } from "./Chi.sol";
import { IUniswapV2Router02 } from "./Uniswap.sol";

contract MintAndSpend {
    Chi public chi;
    address weth;
    IUniswapV2Router02 uniswap;
    address owner;

    constructor()
        public
    {
        owner = msg.sender;

        uniswap = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        weth = uniswap.WETH();

        chi = Chi(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c);

        chi.approve(address(uniswap), uint256(0) - 1);
    }

    modifier profitable(uint256 amount) {
        require(profit(amount) > 0, "transaction is not profitable");
        _;
    }

    function profit(uint256 amount) public view returns (int256) {
        uint256[] memory amountsOut = uniswap.getAmountsOut(amount, path());
        uint256 revenue = amountsOut[amountsOut.length - 1];

        return int256(revenue) - int256(tx.gasprice * gasNeeded(amount));
    }

    function profitableBelow(uint256 amount) public view returns (uint256) {
        uint256[] memory amountsOut = uniswap.getAmountsOut(amount, path());
        uint256 revenue = amountsOut[amountsOut.length - 1];
        // revenue - cost > 0
        // revenue = cost
        // revenue = (tx.gasprice * (overhead + tokens))
        // revenue / (overhead + tokens)

        return revenue / gasNeeded(amount);
    }

    function gasNeeded(uint256 amount) public pure returns (uint256) {
        uint256 overhead = 170000;
        uint256 tokens = 36070 * amount;
        return overhead + tokens;
    }

    function swap(uint256 amount)
        public
        // profitable(amount)
    {
        chi.mint(amount);

        uniswap.swapExactTokensForETH(
            amount,
            0,
            path(),
            address(this),
            uint(0)-1
        );

        (bool success,) = owner.call{ value: address(this).balance }("");
        require(success, "unable to send funds");
    }

    function path()
        internal
        view
        returns (address[] memory pairs)
    {
        pairs = new address[](2);
        pairs[0] = address(chi);
        pairs[1] = weth;
    }

    receive () payable external {
    }
}

