pragma solidity >=0.7.5;
pragma abicoder v2;
import "./interfaces/IUniswapV2Pool.sol";
import './libraries/BytesLib.sol';

contract uniswapv2interface {



    // function uniswap (address tokenIn, address tokenOut, uint amountIn,bytes calldata data) public returns (uint amountOut) {
    function swap (bytes calldata data) public returns (uint amountOut) {
        //"cb9a24c5": "uniswap(bytes)"

        //data:[address(20) pairAdd, ]
        address tokenIn=BytesLib.toAddress(data,0);
        address tokenOut=BytesLib.toAddress(data,20);
        uint amountIn=BytesLib.toUint(data,40);
        address pairAdd=BytesLib.toAddress(data,72);
        //some tokens dont fllow erc20 standard!

        // IERC20(tokenIn).transferFrom(msg.sender, pairAdd, amountIn);
        tokenIn.call(abi.encodeWithSelector(0x23b872dd,msg.sender, pairAdd, amountIn));

        (address token0,address token1) = tokenIn < tokenOut ? (tokenIn,tokenOut):(tokenOut,tokenIn);
        (uint reserve0, uint reserve1,) = IUniswapV2Pair(pairAdd).getReserves();
        (uint reserveIn, uint reserveOut) = tokenIn == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
        IUniswapV2Pair pair=IUniswapV2Pair(pairAdd);
        amountOut = (amountIn*997*reserveOut) / (reserveIn*1000+amountIn*997);
        uint amount0Out = tokenOut == token0 ? amountOut : 0;
        uint amount1Out = tokenOut == token1 ? amountOut : 0;
        pair.swap(amount0Out,amount1Out,address(msg.sender),new bytes(0));
    }
}
