interface IFreeFromUpTo {
    function freeFromUpTo(address from, uint256 value) external returns (uint256 freed);
}

contract Trader {

    address owner;
    IUniswapV2Router02 internal uniswapRouter;
    IUniswapV2Router02 internal sushiswapRouter;
    // address WETH = 0xd0A1E359811322d97991E03f863a0C30C2cF029C; 
    address WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address factory = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;
    IFreeFromUpTo public constant chi = IFreeFromUpTo(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c);
    mapping (address => bool) senders;
    event  Approval(address indexed src, address indexed guy, uint wad);


    constructor() public payable {
        owner = msg.sender;
        // uniswapRouter = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        uniswapRouter = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        sushiswapRouter = IUniswapV2Router02(0xd9e1cE17f2641f24aE83637ab66a2cca9C378B9F);
    }

    // function tradeOnBalancer(address poolAddress, address tokenIn, address tokenOut, uint256 totalAmountIn) public returns (uint tokenAmountOut, uint spotPriceAfter) {
    //     ERC20 token = ERC20(tokenIn);
    //     BPool bPool = BPool(poolAddress);
    //     require(token.approve(poolAddress, totalAmountIn), 'approve failed.');
    //     return bPool.swapExactAmountIn(tokenIn, totalAmountIn, tokenOut, 1, 100000000000000000000000000000000);
    // }

    function swapTokenForEthUniswap(address[] memory path, uint tokenAmountIn, uint ethAmountOutMin) onlySender() public payable {
        ERC20 token = ERC20(path[0]);
        if (token.balanceOf(address(this)) >= tokenAmountIn) {
            token.approve(address(uniswapRouter), tokenAmountIn);
            uniswapRouter.swapExactTokensForETH(tokenAmountIn, ethAmountOutMin, path, address(this), block.timestamp);
        }
    }

    function swapTokenForEthUniswapWithGasToken(address[] memory path, uint tokenAmountIn, uint ethAmountOutMin, uint numGasToken) onlySender() public payable {
        ERC20 token = ERC20(path[0]);
        if (token.balanceOf(address(this)) >= tokenAmountIn) {
            token.approve(address(uniswapRouter), tokenAmountIn);
            uniswapRouter.swapExactTokensForETH(tokenAmountIn, ethAmountOutMin, path, address(this), block.timestamp);
            chi.freeFromUpTo(msg.sender, numGasToken);
        }
    }


    function swapEthForExactTokenUniswap(uint ethAmountInMax, address[] memory path, uint tokenAmountOut) onlySender() public payable {
        if (UniswapV2Library.getAmountsIn(factory, tokenAmountOut, path)[0] <= msg.value) {
            uniswapRouter.swapETHForExactTokens.value(ethAmountInMax)(tokenAmountOut, path, address(this), block.timestamp);
        }
    }

    function swapEthForExactTokenUniswapWithGasToken(uint ethAmountInMax, address[] memory path, uint tokenAmountOut, uint numGasToken) onlySender() public payable {
        if (UniswapV2Library.getAmountsIn(factory, tokenAmountOut, path)[0] <= msg.value) {
            uniswapRouter.swapETHForExactTokens.value(ethAmountInMax)(tokenAmountOut, path, address(this), block.timestamp);
            chi.freeFromUpTo(msg.sender, numGasToken);
        }
    }

    function withdrawToken(address tokenAddress) onlySender() public {
        ERC20 token = ERC20(tokenAddress);
        token.transfer(msg.sender, token.balanceOf(address(this)));
    }

    function withdrawETH(uint amount) onlySender() public {
        msg.sender.send(amount);
    }

    function addSender(address account) onlyOwner public {
        senders[account] = true;
    }

    function removeSender(address account) onlyOwner public {
        senders[account] = false;
    }

    modifier onlyOwner() {
        require (msg.sender == owner, "not owner");
        _;
    }

    modifier onlySender() {
        require (msg.sender == owner || senders[msg.sender], "not sender");
        _;
    }

    fallback() external payable { }

}

