pragma solidity >=0.7.0 <0.8.0;


contract Greeter {
     function Congratulate() public view returns (string memory ){
        return "Happy Birthday, Sam! I wish you to be so successful, insistent, smart as I know you from the first day when I met you!\n\
I wish you to become a multimillionaire by the end of this year, you deserve this!\n\
And I hope your newborn baby will be healthy and will bring a lot of happiness to your life.\n\
\n\
Be healthy and happy!\n\
\n\
For Sam Priestley\n\
From Dmi\n\
24.03.2021";
    }
}
