interface AggregatorV3Interface {

  function decimals() external view returns (uint8);
  function description() external view returns (string memory);
  function version() external view returns (uint256);

  // getRoundData and latestRoundData should both raise "No data present"
  // if they do not have data to report, instead of returning unset values
  // which could be misinterpreted as actual reported values.
  function getRoundData(uint80 _roundId)
    external
    view
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    );
  function latestRoundData()
    external
    view
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    );

}

abstract contract PriceOracle {
    /// @notice Indicator that this is a PriceOracle contract (for inspection)
    bool public constant isPriceOracle = true;

    /**
      * @notice Get the underlying price of a cToken asset
      * @param cToken The cToken to get the underlying price of
      * @return The underlying asset price mantissa (scaled by 1e18).
      *  Zero means the price is unavailable.
      */
    function getUnderlyingPrice(address cToken) external virtual view returns (uint);
}

contract PriceOracleChainlink is PriceOracle , Ownable , ReentrancyGuard {

    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet private _governors;

    // key is the cToken address, value is the priceFeed address
    mapping(address => address) priceFeeds;

    event NewPriceFeed(address cToken, address oldPriceFeed, address newPriceFeed);
    event GovernorAdded(address indexed user);
    event GovernorDeleted(address indexed user);

    constructor()  {
    }

    function setPriceFeed(address cToken, AggregatorV3Interface priceFeed) public  onlyGovernor {
        address oldPriceFeed = priceFeeds[address(cToken)];
        priceFeeds[address(cToken)] = address(priceFeed);
        emit NewPriceFeed(address(cToken), oldPriceFeed, address(priceFeed));
    }

    function getUnderlyingPrice(address cToken) public view virtual override returns (uint) {
        AggregatorV3Interface priceFeed = AggregatorV3Interface(priceFeeds[address(cToken)]);
        (
            , 
            int price,
            ,
            ,
        ) = priceFeed.latestRoundData();
        return uint(price);
    }


    //governor
    function addGovernor(address _governor) public onlyOwner returns (bool) {
        require(_governor != address(0), "_governor is the zero address");

        emit GovernorAdded( _governor );

        return EnumerableSet.add(_governors, _governor);
    }

    function delGovernor(address _governor) public onlyOwner returns (bool) {
        require(_governor != address(0), "_governor is the zero address");

        emit GovernorDeleted( _governor );

        return EnumerableSet.remove(_governors, _governor);
    }

    function getGovernorLength() public view returns (uint256) {
        return EnumerableSet.length(_governors);
    }

    function isGovernor(address account) public view returns (bool) {
        return EnumerableSet.contains(_governors, account);
    }

    function getGovernor(uint256 _index) public view  returns (address){
        require(_index <= getGovernorLength() - 1, "index out of bounds");
        return EnumerableSet.at(_governors, _index);
    }

    // modifier for mint function
    modifier onlyGovernor() {
        require(isGovernor(msg.sender) || owner() == msg.sender, "caller is not the governor");
        _;
    }

}
