contracts/2_Owner.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

import "./IERC20.sol";

/**
 * @title Owner
 * @dev Set & change owner
 */
contract Distribute {

    struct Beneficiary {
        address addr;
        uint256 amount;
    }

    address private owner;
    address private sidePocket;
    IERC20 private token;

    // modifier to check if caller is owner
    modifier onlyOwner() {
        // If the first argument of 'require' evaluates to 'false', execution terminates and all
        // changes to the state and to Ether balances are reverted.
        // This used to consume all gas in old EVM versions, but not anymore.
        // It is often a good idea to use 'require' to check if functions are called correctly.
        // As a second argument, you can also provide an explanation about what went wrong.
        require(msg.sender == owner, "not owner");
        _;
    }

    /**
     * @dev Set contract deployer as owner
     */
    constructor(IERC20 _token, address _sidePocket) {
        owner = msg.sender; // 'msg.sender' is sender of current call, contract deployer for a constructor
        token = _token;
        sidePocket = _sidePocket;
    }

    /**
     * @dev Change owner
     * @param newOwner address of new owner
     */
    function changeOwner(address newOwner) public onlyOwner {
        owner = newOwner;
    }

    function changeToken(IERC20 _token) public onlyOwner {
        token = _token;
    }

    function changeSidePocket(address _sidePocket) public onlyOwner {
        sidePocket = _sidePocket;
    }

    function distribute(Beneficiary[] memory beneficiaries) public onlyOwner {
        for(uint i = 0; i < beneficiaries.length ; i++) {
            token.transferFrom(sidePocket, beneficiaries[i].addr, beneficiaries[i].amount);
        }
    }

    /**
     * @dev Return owner address 
     * @return address of owner
     */
    function getOwner() external view returns (address) {
        return owner;
    }

    /**
     * @dev Return owner address 
     * @return address of owner
     */
    function getToken() external view returns (IERC20) {
        return token;
    }

    function getSidePocket() external view returns (address) {
        return sidePocket;
    }
}

