            // solhint-disable-next-line max-line-length
            require(abi.decode(returndata, (bool)), "SafeERC20: ERC20 operation did not succeed");
        }
    }
}

contract TokenStakeForEth is Ownable {
    using SafeERC20 for IERC20;
    using Address for address;
    using SafeMath for uint256;

    struct Pool {
        address tokenAddress;
        uint256 stakeAmount;
        uint256 stakeTime;
        address nftAddress;
        uint256 leftCount;
        uint256 startTime;
        bool closed;
    }
    Pool[] public pool;

    mapping (address => uint256) public nftTokenIds;

    // pool id => user address => stake info list (start staking time)
    mapping (uint256 => mapping (address => uint256[])) public user;

    event AddPoolEvent(address indexed tokenAddress, uint256 indexed stakeAmount, uint256 stakeTime, address indexed nftAddress, uint256 leftCount, uint256 startTime);
    event AddNFTTokenIdEvent(address indexed _nftAddress, uint256 indexed _tokenId);
    event AddNFT2PoolEvent(uint256 indexed pid, uint256[] tokenIds);
    event StakeEvent(uint256 indexed pid, address indexed user, uint256 beginTime);
    event ForceWithdrawEvent(uint256 indexed pid, address indexed user, uint256 indexed beginTime);
    event HarvestEvent(uint256 indexed pid, address indexed user, uint256 indexed tokenId);
    event TransferNFTOwnership(address indexed _nftAddress, address indexed _newOwner);
    event NominateNFTPotentialOwner(address indexed _nftAddress, address indexed _newOwner);
    event AcceptNFTOwnership(address indexed _nftAddress);
    event UpdatePoolStatusEvent(uint256 indexed pid, bool closed);

    modifier validatePoolByPid(uint256 _pid) {
        require(_pid < pool.length, "pool does not exist");
        _;
    }

    constructor() {

    }

    function addPool(address _tokenAddress, uint256 _stakeAmount, uint256 _stakeTime, address _nftAddress, uint256 _leftCount, uint256 _startTime) external onlyOwner {
        require(_tokenAddress.isContract(), "stake token address should be smart contract address");
        require(_nftAddress.isContract(), "NFT address should be smart contract address");

        IERC721 nft = IERC721(_nftAddress);
        require(!nft.exists(nftTokenIds[_nftAddress]), "NFT already exists");

        pool.push(Pool({
            tokenAddress: _tokenAddress,
            stakeAmount: _stakeAmount,
            stakeTime: _stakeTime,
            nftAddress: _nftAddress,
            leftCount: _leftCount,
            startTime: _startTime,
            closed: false
        }));

        emit AddPoolEvent(_tokenAddress, _stakeAmount, _stakeTime, _nftAddress, _leftCount, _stakeTime);
    }

    function addNFTTokenId(address _nftAddress, uint256 _tokenId) external onlyOwner {
        require(_nftAddress.isContract(), "stake NFT address should be smart contract address");
        IERC721 nft = IERC721(_nftAddress);
        require(!nft.exists(_tokenId), "NFT already exists");
        nftTokenIds[_nftAddress] = _tokenId;

        emit AddNFTTokenIdEvent(_nftAddress, _tokenId);
    }

    function stake(uint256 _pid) external validatePoolByPid(_pid) {
        require(!pool[_pid].closed, "pool is closed");
        require(pool[_pid].startTime <= block.timestamp, "not start");
        require(pool[_pid].leftCount > 0, "no available");

        IERC20 token = IERC20(pool[_pid].tokenAddress);
        require(token.balanceOf(msg.sender) >= pool[_pid].stakeAmount, "out of balance");
        require(token.allowance(msg.sender, address(this)) >= pool[_pid].stakeAmount, "not enough permission to stake token");

        user[_pid][msg.sender].push(block.timestamp);
        pool[_pid].leftCount--;

        token.safeTransferFrom(msg.sender, address(this), pool[_pid].stakeAmount);

        emit StakeEvent(_pid, msg.sender, block.timestamp);
    }

    function forceWithdraw(uint256 _pid, uint256 _sid) external validatePoolByPid(_pid) {
        require(_sid < user[_pid][msg.sender].length, "staking is not existed");
        uint256 beginTime = user[_pid][msg.sender][_sid];
        require(block.timestamp < beginTime + pool[_pid].stakeTime, "staking is ended");

        IERC20 token = IERC20(pool[_pid].tokenAddress);
        require(token.balanceOf(address(this)) >= pool[_pid].stakeAmount, "out of contract balance");
        removeFromUserList(_pid, _sid);
        pool[_pid].leftCount++;

        token.safeTransfer(msg.sender, pool[_pid].stakeAmount);

        emit ForceWithdrawEvent(_pid, msg.sender, beginTime);
    }

    function harvest(uint256 _pid, uint256 _sid) external validatePoolByPid(_pid) {
        require(_sid < user[_pid][msg.sender].length, "staking is not existed");
        require(block.timestamp >= user[_pid][msg.sender][_sid] + pool[_pid].stakeTime, "staking is not due");

        IERC721 nft = IERC721(pool[_pid].nftAddress);
        require(nft.isAdminOfMintBurn(address(this)), "no permission to mint reward NFT");
        uint256 tokenId = nftTokenIds[pool[_pid].nftAddress];
        require(!nft.exists(tokenId), "NFT already exists");
        nftTokenIds[pool[_pid].nftAddress]++;

        IERC20 token = IERC20(pool[_pid].tokenAddress);
        require(token.balanceOf(address(this)) >= pool[_pid].stakeAmount, "out of contract balance");
        removeFromUserList(_pid, _sid);

        token.safeTransfer(msg.sender, pool[_pid].stakeAmount);
        nft.safeMint(msg.sender, tokenId);

        emit HarvestEvent(_pid, msg.sender, tokenId);
    }

    function withdrawTokenByOwner(uint256 _pid, uint256 _sid, address _to) external onlyOwner validatePoolByPid(_pid) {
        require(_sid < user[_pid][_to].length, "staking is not existed");

        IERC20 token = IERC20(pool[_pid].tokenAddress);
        uint256 amount = pool[_pid].stakeAmount <= token.balanceOf(address(this)) ? pool[_pid].stakeAmount : token.balanceOf(address(this));
        removeFromUserList(_pid, _sid);

        token.safeTransfer(_to, amount);
    }

    function getUserStakeHisCnt(uint256 _pid, address _userAddr) public view validatePoolByPid(_pid) returns(uint) {
        return user[_pid][_userAddr].length;
    }

    function getUserStakeHis(uint256 _pid, address _userAddr, uint256 _index, uint256 _time) public view validatePoolByPid(_pid) returns(uint256, uint256, uint256, bool) {
        require(_index < user[_pid][_userAddr].length, "staking is not existed");
        uint256 endTime = user[_pid][_userAddr][_index] + pool[_pid].stakeTime;
        bool isCompleted = false;
        if (_time >= endTime) {
            isCompleted = true;
        }
        return (pool[_pid].stakeAmount, user[_pid][_userAddr][_index], endTime, isCompleted);
    }

    function getPoolAmount(uint256 _pid) public view validatePoolByPid(_pid) returns(uint256) {
        return pool[_pid].stakeAmount;
    }

    function transferNFTOwnership(address _nftAddress, address _newOwner) external onlyOwner {
        IERC721 nft = IERC721(_nftAddress);
        nft.transferOwnership(_newOwner);

        emit TransferNFTOwnership(_nftAddress, _newOwner);
    }

    function nominateNFTPotentialOwner(address _nftAddress, address _newOwner) external onlyOwner {
        IERC721 nft = IERC721(_nftAddress);
        nft.nominatePotentialOwner(_newOwner);

        emit NominateNFTPotentialOwner(_nftAddress, _newOwner);
    }

    function acceptNFTOwnership(address _nftAddress) external onlyOwner {
        IERC721 nft = IERC721(_nftAddress);
        nft.acceptOwnership();

        emit AcceptNFTOwnership(_nftAddress);
    }

    function getPoolNFTLeft(uint256 _pid) public view validatePoolByPid(_pid) returns(uint256) {
        return pool[_pid].leftCount;
    }

    function updatePoolStatus(uint256 _pid, bool _closed) external onlyOwner validatePoolByPid(_pid) {
        pool[_pid].closed = _closed;

        emit UpdatePoolStatusEvent(_pid, _closed);
    }

    function updatePoolLeftCount(uint256 _pid, uint256 _leftCount) external onlyOwner validatePoolByPid(_pid) {
        pool[_pid].leftCount = _leftCount;
    }

    function updatePoolNFTAddress(uint256 _pid, address _nftAddress) external onlyOwner validatePoolByPid(_pid) {
        pool[_pid].nftAddress = _nftAddress;
    }

    function updatePoolStakeTime(uint256 _pid, uint256 _stakeTime) external onlyOwner validatePoolByPid(_pid) {
        pool[_pid].stakeTime = _stakeTime;
    }

    function updatePoolStartTime(uint256 _pid, uint256 _startTime) external onlyOwner validatePoolByPid(_pid) {
        pool[_pid].startTime = _startTime;
    }

    function removeFromUserList(uint256 _pid, uint _sid) private {
        user[_pid][msg.sender][_sid] = user[_pid][msg.sender][user[_pid][msg.sender].length - 1];
        user[_pid][msg.sender].pop();
    }

}
