pragma solidity ^0.8.0;

interface Pool {
    function skim(address to) external;
}

contract Scimitar4 {

    address internal immutable owner;

    constructor() {
        owner = msg.sender;
    }

    // f7836435186477227(address) 0x00000000
    function f7836435186477227(address _pool) external payable {
        require(block.number == msg.value, 'r777');
        require(msg.sender == owner);
        Pool(_pool).skim(owner);
    }

    function withdraw() external {
        payable(owner).transfer(address(this).balance);
    }

}

