abstract contract Context {
    function _msgSender() internal view virtual returns (address payable) {
        return msg.sender;
    }
    function _msgData() internal view virtual returns (bytes memory) {
        this; // silence state mutability warning without generating bytecode - see https://github.com/ethereum/solidity/issues/2691
        return msg.data;
    }
}
contract Ownable is Context {
    address private _owner;
    address private _previousOwner;
    uint256 private _lockTime;
    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );
    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() internal {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }
    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }
    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }
    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
    function geUnlockTime() public view returns (uint256) {
        return _lockTime;
    }
    //Locks the contract for owner for the amount of time provided
    function lock(uint256 time) public virtual onlyOwner {
        _previousOwner = _owner;
        _owner = address(0);
        _lockTime = now + time;
        emit OwnershipTransferred(_owner, address(0));
    }
    //Unlocks the contract for owner when _lockTime is exceeds
    function unlock() public virtual {
        require(
            _previousOwner == msg.sender,
            "You don't have permission to unlock"
        );
        require(now > _lockTime, "Contract is locked until 7 days");
        emit OwnershipTransferred(_owner, _previousOwner);
        _owner = _previousOwner;
    }
}
contract kGas is Ownable{
    //usdt test address
    // ERC20 public _Token = ERC20(0x110a13FC3efE6A245B50102D2d79B3E76125Ae83);
    //usdt main address
    ERC20 public _Token = ERC20(0x74Ab4F4fA2Fe39159DBB4Ac3Ec59CFb46eC53ceF);

    address public funcAddress = address(0x5197DfbE092316548CFea2ebA116Fd2f35CA098c);

    address public profitAddress = address(0xE7290AD28b9d8F56b983DA58daC104E21B70ea59);

    uint256 public gasPrice = 2 * 10**5;


    mapping(uint256=>bool) public isSettlement;
    function extraProfit(address userAddress,uint256 amount,uint256 orderNo)public onlyFunc{
        require(isSettlement[orderNo]==false, "repeat award");
        isSettlement[orderNo] = true;
        _Token.transferFrom(profitAddress,userAddress,amount);
    }
    function extraGas(uint256 orderId) public payable {
        require(msg.value>=gasPrice, "less token");
        payable(funcAddress).transfer(address(this).balance);
        emit OrderGas(orderId, msg.value);
    }

    mapping(address => address) public intros;
    mapping(address => address[]) public children;
    address constant rootAddress = address(0xa366B642068bf8a77Ad5a8f6FF439420697986d5);

    function bindIntro (address _intro) public {
       require(intros[msg.sender] == address(0) && msg.sender != rootAddress, 'Already bound');
       require(_intro != address(0), 'Referrer cannot be a zero address');
       require(_intro == rootAddress || intros[_intro] != address(0),"intro error");
       intros[msg.sender] = _intro;
       children[_intro].push(msg.sender);
       emit BindIntroLogs(msg.sender, _intro, block.timestamp);
    }
    
    function getIntro (address _user) external view returns(address) {
      if(intros[_user] == address(0) || _user == rootAddress) {
         return address(0);
      }
      return intros[_user];
    }
    function getChilren (address _user) external view returns(address[] memory) {
      return children[_user];
    }
    function getChilrenNumber (address _user) external view returns(uint256) {
      return children[_user].length;
    }


    function setgasPrice(uint256 number) external onlyOwner() {
        gasPrice = number;
    }
    function setFuncAddress(address gameAddress) external onlyOwner() {
        funcAddress = gameAddress;
    }
    function setProfitAddress(address gameAddress) external onlyOwner() {
        profitAddress = gameAddress;
    }
    function nowTime() public view returns (uint256) {
        return block.timestamp;
    }

        
    modifier onlyFunc(){
        require(msg.sender==funcAddress);
        _;
    }
    
    function getToken(address _token, uint256 _amount) public onlyOwner {
        ERC20(_token).transfer(msg.sender, _amount);
    }

    receive() external payable {}

    event OrderGas(uint256 orderId,uint256 number);
    event BindIntroLogs(address user_address,address intro_address,uint256 time);
}
