// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.7.4;

interface UniswapV2Factory {
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

interface UniswapV2ERC20 {
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);
    function transfer(address to, uint value) external returns (bool);
    function approve(address spender, uint value) external returns (bool);
}

interface UniswapV2Pair {
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function token0() external view returns (address);
}

interface UniswapV2Router02 {
    function swapExactTokensForTokensSupportingFeeOnTransferTokens(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external;
    function swapExactTokensForTokens(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external returns (uint[] memory amounts);
    function getAmountsOut(uint amountIn, address[] memory path) external view returns (uint[] memory amounts);
}

contract DracoExec {

    address owner;

    mapping (address => bool) whitelist;

    modifier onlyOwner {
        require(msg.sender == owner, "Must be Owner");
        _;
    }

    modifier onlyWhitelist(address _tocheck) {
        require(whitelist[_tocheck], "Address is not in whitelist");
        _;
    }

    struct Data {
        uint MAX_INT;
        uint _amount_in;
        uint _amount_out_min;
        uint[] amount_out_list;
        address pair;
        uint112 reserve0;
        uint112 reserve1;
        uint _balance;
    }

    constructor() {
        owner = msg.sender;
        whitelist[msg.sender] = true;
    }

    function getOwner() external view onlyOwner returns(address) {
        return owner;
    }

    function addWhitelist(address _to_add) external onlyOwner {
        whitelist[_to_add] = true;
    }

    function removeWhitelist(address _to_remove) external onlyOwner {
        whitelist[_to_remove] = false;
    }

    function getWhitelist(address _to_get) external view onlyOwner returns(bool) {
        return whitelist[_to_get];
    }

    function transferToken(address _token, address _to, uint _amount) external onlyOwner {
        UniswapV2ERC20(_token).transfer(_to, _amount);
    }

    function changeOwner(address _new_owner) external onlyOwner {
        owner = _new_owner;
    }

    function withdrawToken(address _token, uint _amount) external onlyOwner {
        UniswapV2ERC20(_token).transfer(msg.sender, _amount);
    }

    function dracoTrade(address _factory, address _router, address _weth, uint _reserveA, uint _reserveB, uint _min_front, address[] memory _path, uint _deadline, uint _min_child_gas, bool _live) external onlyWhitelist(msg.sender) {

        Data memory data;

        data.MAX_INT = 115792089237316195423570985008687907853269984665640564039457584007913129639935;
        data._amount_in = 0;
        data._amount_out_min = 0;

        if (UniswapV2ERC20(_path[0]).allowance(address(this), _router) == 0) {
            UniswapV2ERC20(_path[0]).approve(_router, data.MAX_INT);
        }
        if (_path[0] != _weth) {
            data._amount_in = UniswapV2ERC20(_path[0]).balanceOf(address(this));
            if (data._amount_in == 0) {
                revert();
            }
            data.amount_out_list = UniswapV2Router02(_router).getAmountsOut(data._amount_in, _path);
            data._amount_out_min = data.amount_out_list[data.amount_out_list.length - 1];
        }
        else if (_path[0] == _weth) {

            data.pair = UniswapV2Factory(_factory).getPair(_path[0],_path[1]);
            (data.reserve0, data.reserve1, ) = UniswapV2Pair(data.pair).getReserves();

            if (UniswapV2Pair(data.pair).token0() != _weth) {
                (data.reserve0, data.reserve1) = (data.reserve1, data.reserve0);
            }

            if (data.reserve0 * data.reserve1 < _reserveA * _reserveB || data.reserve0 > _reserveA || _reserveA - data.reserve0 < _min_front) {
                revert();
            }

            data._amount_in = _reserveA - data.reserve0;

            data._balance = UniswapV2ERC20(_path[0]).balanceOf(address(this));
            if (data._balance < data._amount_in) {
                if (data._balance < _min_front) {
                    revert();
                }
                data._amount_in = data._balance;
                data.amount_out_list = UniswapV2Router02(_router).getAmountsOut(data._amount_in, _path);
                data._amount_out_min = data.amount_out_list[data.amount_out_list.length - 1];
            }
            else {
                data._amount_in = data._amount_in * 9999 / 10000;
            }
        }
        else {
            revert();
        }

        if (_live) {
            UniswapV2Router02(_router).swapExactTokensForTokensSupportingFeeOnTransferTokens(data._amount_in, data._amount_out_min*9998/10000, _path, address(this), _deadline);
        }

        if (_path[0] != _weth) {
            uint child_balance = UniswapV2ERC20(_path[1]).balanceOf(msg.sender);
            if (child_balance < _min_child_gas) {
                address[] memory __path;
                __path[0] = _path[1];
                __path[1] = _path[1];
                UniswapV2Router02(_router).swapExactTokensForETH(_min_child_gas, _min_child_gas, __path, msg.sender, _deadline);
            }
        }

    }

    function killMe() external onlyOwner {
        selfdestruct(msg.sender);
    }

}
