// a library for handling binary fixed point numbers (https://en.wikipedia.org/wiki/Q_(number_format))
library FixedPoint {
    // range: [0, 2**112 - 1]
    // resolution: 1 / 2**112
    struct uq112x112 {
        uint224 _x;
    }

    // range: [0, 2**144 - 1]
    // resolution: 1 / 2**112
    struct uq144x112 {
        uint _x;
    }

    uint8 private constant RESOLUTION = 112;

    // encode a uint112 as a UQ112x112
    function encode(uint112 x) internal pure returns (uq112x112 memory) {
        return uq112x112(uint224(x) << RESOLUTION);
    }

    // encodes a uint144 as a UQ144x112
    function encode144(uint144 x) internal pure returns (uq144x112 memory) {
        return uq144x112(uint256(x) << RESOLUTION);
    }

    // divide a UQ112x112 by a uint112, returning a UQ112x112
    function div(uq112x112 memory self, uint112 x) internal pure returns (uq112x112 memory) {
        require(x != 0, 'FixedPoint: DIV_BY_ZERO');
        return uq112x112(self._x / uint224(x));
    }

    // multiply a UQ112x112 by a uint, returning a UQ144x112
    // reverts on overflow
    function mul(uq112x112 memory self, uint y) internal pure returns (uq144x112 memory) {
        uint z;
        require(y == 0 || (z = uint(self._x) * y) / y == uint(self._x), "FixedPoint: MULTIPLICATION_OVERFLOW");
        return uq144x112(z);
    }

    // returns a UQ112x112 which represents the ratio of the numerator to the denominator
    // equivalent to encode(numerator).div(denominator)
    function fraction(uint112 numerator, uint112 denominator) internal pure returns (uq112x112 memory) {
        require(denominator > 0, "FixedPoint: DIV_BY_ZERO");
        return uq112x112((uint224(numerator) << RESOLUTION) / denominator);
    }

    // decode a UQ112x112 into a uint112 by truncating after the radix point
    function decode(uq112x112 memory self) internal pure returns (uint112) {
        return uint112(self._x >> RESOLUTION);
    }

    // decode a UQ144x112 into a uint144 by truncating after the radix point
    function decode144(uq144x112 memory self) internal pure returns (uint144) {
        return uint144(self._x >> RESOLUTION);
    }
}

// library with helper methods for oracles that are concerned with computing average prices
library UniswapV2OracleLibrary {
    using FixedPoint for *;

    // helper function that returns the current block timestamp within the range of uint32, i.e. [0, 2**32 - 1]
    function currentBlockTimestamp() internal view returns (uint32) {
        return uint32(block.timestamp % 2 ** 32);
    }

    // produces the cumulative price using counterfactuals to save gas and avoid a call to sync.
    function currentCumulativePrices(
        address pair
    ) internal view returns (uint price0Cumulative, uint price1Cumulative, uint32 blockTimestamp) {
        blockTimestamp = currentBlockTimestamp();
        price0Cumulative = IUniswapV2Pair(pair).price0CumulativeLast();
        price1Cumulative = IUniswapV2Pair(pair).price1CumulativeLast();

        // if time has elapsed since the last update on the pair, mock the accumulated price values
        (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast) = IUniswapV2Pair(pair).getReserves();
        if (blockTimestampLast != blockTimestamp) {
            // subtraction overflow is desired
            uint32 timeElapsed = blockTimestamp - blockTimestampLast;
            // addition overflow is desired
            // counterfactual
            price0Cumulative += uint(FixedPoint.fraction(reserve1, reserve0)._x) * timeElapsed;
            // counterfactual
            price1Cumulative += uint(FixedPoint.fraction(reserve0, reserve1)._x) * timeElapsed;
        }
    }
}


interface IOracle {
    function update() external;
    function estimateRequirement(address account, uint256 hashrate) external view returns(uint256, uint256, uint256);
}


interface IZooPool {
    function rewardRate() external view returns(uint256);
}


contract OracleV2 is IOracle, Ownable {
    using SafeMath for uint256;
    using FixedPoint for *;

    IUniswapV2Pair public constant Pair1 = IUniswapV2Pair(0xf52cC785D5F3E7D6FF0AD4e665a929b84F69e7c5); // HOE_ETH_Pair
    IUniswapV2Pair public constant Pair2 = IUniswapV2Pair(0x0d4a11d5EEaaC28EC3F61d100daF4d40471f1852); // ETH_USDT_Pair

    uint256 public timeGap;
    uint256 public lastUpdateTime;

    uint256 public pair1LastPrice0AmountOut;
    uint256 public pair2LastPrice0AmountOut;
    uint256 public pair1LastPrice0Cumulative;
    uint256 public pair2LastPrice0Cumulative;

    uint32 public pair1LastBlockTimestamp;
    uint32 public pair2LastBlockTimestamp;

    uint256 private constant BASE_REWARD_RATE = 2314814814814814;
    IZooPool public zooPool;

    constructor(uint256 _timeGap, address _zooPool) public {
        timeGap = _timeGap;
        (pair1LastPrice0Cumulative, , pair1LastBlockTimestamp) = UniswapV2OracleLibrary.currentCumulativePrices(address(Pair1));
        (pair2LastPrice0Cumulative, , pair2LastBlockTimestamp) = UniswapV2OracleLibrary.currentCumulativePrices(address(Pair2));
        lastUpdateTime = block.timestamp;
        zooPool = IZooPool(_zooPool);
    }

    function setTimeGap(uint256 _timeGap) public onlyOwner {
        require(_timeGap > 0);
        timeGap = _timeGap;
    }

    function _estimatePair1Token0AmountOut() internal view returns (uint256 priceCumulative, uint32 blockTimestamp, uint256 amountOut) {
        (priceCumulative, , blockTimestamp) = UniswapV2OracleLibrary.currentCumulativePrices(address(Pair1));
        uint256 timeElapsed = blockTimestamp - pair1LastBlockTimestamp;
        if (timeElapsed <= timeGap) {
            priceCumulative = pair1LastPrice0Cumulative;
            blockTimestamp = pair1LastBlockTimestamp;
            amountOut = pair1LastPrice0AmountOut;
        } else {
            // overflow is desired, casting never truncates
            // cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
            uint224 priceAverage = uint224((priceCumulative - pair1LastPrice0Cumulative) / timeElapsed);
            amountOut = FixedPoint.uq112x112(priceAverage).mul(1e18).decode144();
        }
    }

    function _estimatePair2Token0AmountOut() internal view returns (uint256 priceCumulative, uint32 blockTimestamp, uint256 amountOut) {
        (priceCumulative, , blockTimestamp) = UniswapV2OracleLibrary.currentCumulativePrices(address(Pair2));
        uint256 timeElapsed = blockTimestamp - pair2LastBlockTimestamp;
        if (timeElapsed <= timeGap) {
            priceCumulative = pair2LastPrice0Cumulative;
            blockTimestamp = pair2LastBlockTimestamp;
            amountOut = pair2LastPrice0AmountOut;
        } else {
            // overflow is desired, casting never truncates
            // cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
            uint224 priceAverage = uint224((priceCumulative - pair2LastPrice0Cumulative) / timeElapsed);
            amountOut = FixedPoint.uq112x112(priceAverage).mul(1e18).decode144();
        }
    }

    function _update() internal {
        (uint256 pair1Price0Cumulative, uint32 pair1Price0Timestamp, uint256 pair1Price0) = _estimatePair1Token0AmountOut();
        (uint256 pair2Price0Cumulative, uint32 pair2Price0Timestamp, uint256 pair2Price0) = _estimatePair2Token0AmountOut();
        pair1LastPrice0Cumulative = pair1Price0Cumulative;
        pair2LastPrice0Cumulative = pair2Price0Cumulative;
        pair1LastBlockTimestamp = pair1Price0Timestamp;
        pair2LastBlockTimestamp = pair2Price0Timestamp;
        pair1LastPrice0AmountOut = pair1Price0;
        pair2LastPrice0AmountOut = pair2Price0;
    }

    function update() external override {
        uint timestamp = block.timestamp;
        if (timestamp < lastUpdateTime) {
            return;
        }
        if (timestamp - lastUpdateTime <= timeGap) {
            return;
        }

        _update();
        lastUpdateTime = timestamp;
    }

    function estimateRequirement(address account, uint256 hashrate) external override view returns (uint256, uint256, uint256){
        uint256 price = estimateTokenPrice();
        uint256 amount = hashrate.mul(1e6).mul(1e18).mul(1e18).div(price);
        uint256 userHashrate = hashrate.mul(multiplier());
        uint256 referReward = hashrate;
        return (userHashrate, amount, referReward);
    }

    function estimateTokenPrice() public view returns (uint256) {
        (,,uint256 pair1Price0) = _estimatePair1Token0AmountOut();
        (,,uint256 pair2Price0) = _estimatePair2Token0AmountOut();
        return pair1Price0 * pair2Price0;
    }

    function multiplier() public view returns (uint256) {
        uint256 zooPoolRewardRate = zooPool.rewardRate();
        return BASE_REWARD_RATE.div(zooPoolRewardRate);
    }
}
