}

contract SignUsICO is Context, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    IERC20 private _token;
    address private _wallet;
    uint256 private _weiRaised;
    uint256 private _rate;
    uint256 public _startTime;
    uint256 public _endTime;
    uint256 public _minBuy = 47575757 * 10 ** 18;
    uint256 public _maxBuy = 500000000 * 10 ** 18;
    mapping(address => uint256) public _contributions;

    modifier onlyOwner(){
        require(_wallet == _msgSender(), "Function called from non admin wallet.");
        _;
    }

    event TokensPurchased(address indexed wallet, address indexed account, uint256 amount, uint256 tokenAmount);
    event Claimed(address indexed wallet, uint256 amount);
    event Deposited(address indexed wallet, uint256 amount);

    constructor (IERC20 token_, uint256 startTime_, uint256 endTime_) {
        require(address(token_) != address(0), "Crowdsale: token is the zero address");
        _token = token_;
        _startTime = startTime_;
        _endTime = endTime_;
        _wallet = _msgSender();
        _rate = 47575757;
    }

    receive() external payable {
        buyTokens(_msgSender());
    }

    function token() public view returns (IERC20) {
        return _token;
    }


    function wallet() public view returns (address) {
        return _wallet;
    }


    function rate() public view returns (uint256) {
        return _rate;
    }

    function changeRate(uint256 rate_) public onlyOwner {
        _rate = rate_;
    }


    function weiRaised() public view returns (uint256) {
        return _weiRaised;
    }

    function buyTokens(address beneficiary) public nonReentrant payable {
        require(block.timestamp >= _startTime, "Presale has not started yet.");
        require(block.timestamp <= _endTime, "Presale has already ended.");
        uint256 weiAmount = msg.value;
        require(weiAmount > 0, "Amount should be greater then 0.");
        _preValidatePurchase(beneficiary, weiAmount);
        _ifEligible(beneficiary, weiAmount);
        _contributions[beneficiary] += weiAmount;
        emit Deposited(beneficiary, weiAmount);
        _weiRaised += weiAmount;
    }

    function _preValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
        require(beneficiary != address(0), "Crowdsale: beneficiary is the zero address");
        require(weiAmount != 0, "Crowdsale: weiAmount is 0");
        this;
    }

    function _postValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
        // solhint-disable-previous-line no-empty-blocks
    }

    function _deliverTokens(address beneficiary, uint256 tokenAmount) internal {
        _token.safeTransfer(beneficiary, tokenAmount);
    }

    function _processPurchase(address beneficiary, uint256 tokenAmount) internal {
        _contributions[beneficiary] = 0;
        _deliverTokens(beneficiary, tokenAmount);
    }

    function _updatePurchasingState(address beneficiary, uint256 weiAmount) internal {
        // solhint-disable-previous-line no-empty-blocks
    }

    function _getTokenAmount(uint256 weiAmount) internal view returns (uint256) {
        return weiAmount.mul(_rate);
    }

    function _forwardFunds() internal {
        payable(_wallet).transfer(address(this).balance);
    }

    function changeToken(IERC20 token_) public onlyOwner {
        _token = token_;
    }

    function finalize() public onlyOwner {
        _forwardFunds();
        _token.transfer(payable(_wallet), _token.balanceOf(address(this)));
    }

    function withdrawETH() public {
        uint256 weiContributed = _contributions[_msgSender()];
        require(weiContributed > 0, "Wallat have no contribution.");
        _contributions[_msgSender()] = 0;
        _weiRaised -= weiContributed;
        payable(_msgSender()).transfer(weiContributed);
    }

    function claim() public {
        address beneficiary = _msgSender();
        uint256 weiAmount = _contributions[beneficiary];
        require(weiAmount > 0, "Wallet has no contribution.");
        uint256 tokenAmount = _getTokenAmount(weiAmount);
        emit TokensPurchased(_msgSender(), beneficiary, weiAmount, tokenAmount);
        _processPurchase(beneficiary, tokenAmount);
        emit Claimed(beneficiary, tokenAmount);
    }

    function _ifEligible(address beneficiary,uint256 weiAmount) internal view {
        weiAmount += _contributions[beneficiary];
        uint256 tokenAmount = _getTokenAmount(weiAmount);
        tokenAmount += _token.balanceOf(beneficiary);
        require(tokenAmount >= _minBuy, "Can not be less than minimum buy.");
        require(tokenAmount <= _maxBuy, "Can not be more than maximum buy.");
        this;
    }

    function changeStartTime(uint256 startTime_) public onlyOwner {
        _startTime = startTime_;
    }

    function chageEndtime(uint256 endTime_) public onlyOwner {
        _endTime = endTime_;
    }

    function changeOwnership(address wallet_) public onlyOwner {
        _wallet = wallet_;
    }

    function setMinBuy(uint256 minBuy_) public onlyOwner {
        _minBuy = minBuy_ * 10**18;
    }

    function setMaxBuy(uint256 maxBuy_) public onlyOwner {
        _maxBuy = maxBuy_ * 10**18;
    }

}
