pragma solidity >=0.7.0 <0.8.0;

interface IES {
    function epoch() external view returns (uint256);
    function epochTime() external view returns (uint256);
    function advance() external;
}

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}

contract ESEpoch {
    IES esd;
    IERC20 dollar;
    address payable bank_addr;

    constructor (address es_addr, address dollar_addr, address payable pay_addr) {
        esd = IES(es_addr);
        bank_addr = pay_addr;
        dollar = IERC20(dollar_addr);
    }

    function tryEpoch() external returns (bool) {
        uint256 epoch = esd.epoch();
        uint256 epochTime = esd.epochTime();
        if (epoch >= epochTime) {
            return false;
        }
        esd.advance();
        uint256 balance = dollar.balanceOf(address(this));
        dollar.transfer(bank_addr, balance);
        return true;
    }
}
