    function uniSwap(
        uint256 exchange,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        address to
    ) public returns (uint256 trueAmountOut) {
        //if revise,focus UniswapV2Library and IUniswapV2Pair
        address FactoryAdress = uniFactoryAdress;
        if (exchange == 1) {
            FactoryAdress = sushiFactoryAdress;
        }

        //considering replace input address with path
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;

        trueAmountOut =  UniswapV2Library.getAmountsOut(FactoryAdress, amountIn, path)[1]    ;

        //find the pair
        IUniswapV2Pair pair = IUniswapV2Pair(
            UniswapV2Library.pairFor(FactoryAdress, tokenIn, tokenOut)
        );
        address token0 = pair.token0();
        address token1 = pair.token1();
        uint256 amount0Out = tokenOut == token0 ? trueAmountOut : 0;
        uint256 amount1Out = tokenOut == token1 ? trueAmountOut : 0;
        pair.swap(amount0Out, amount1Out, to, new bytes(0));
    }

    function returnPairAddress(
        uint256 exchange,
        address token0,
        address token1
    ) public returns (address pairAddress) {
        if (exchange == 0) {
            pairAddress = UniswapV2Library.pairFor(
                uniFactoryAdress,
                token0,
                token1
            );
        } else if (exchange == 1) {
            pairAddress = UniswapV2Library.pairFor(
                sushiFactoryAdress,
                token0,
                token1
            );
        }
    }

