pragma solidity ^0.8.2;
pragma experimental ABIEncoderV2;

//SPDX-License-Identifier: UNLICENSED

import "./libs/Bancor.sol";
import "./libs/SafeMath.sol";
import "./libs/Dydx.sol";
import "./libs/Interfaces.sol";
import "./libs/UniswapV2.sol";
import "./libs/Zeroex.sol";
import "./libs/Balancer.sol";
import "./libs/Aave.sol";
import "./libs/Curve.sol";

contract Implementation {
    using SafeMath for uint256;
    using Address for address;
    
    struct Swap {
        uint protocol;
        address swapContract;
        address poolContract;
        address tokenAcontract;
        address tokenBcontract;
        uint swapFees;
        uint zeroExOrderID;
    }
    
    struct SequenceParams {
        uint targetBlock;
        bool WithLoan;
        bool UseChi;
        uint LoanProtocolID;
        address LoanContract;
        address LoanToken;
        address RepayToken;
        uint initialAmountIn;
        uint minAmountOut;
        uint estGasCost;
        uint swapFees;
    }
    
    event BalancerDebug(address indexed pool, uint balTokenIn, uint dwTokenIn, uint balTokenOut, uint dwTokenOut, uint inputAmount, uint outputAmount);
    event UniswapDebug(address indexed pool, uint balTokenIn, uint balTokenOut, uint inputAmount, uint outputAmount);
    event BancorDebug(address[] path, uint inputAmount, uint outputAmount);
    event CurveDebug(uint inputAmount, uint outputAmount);
    event ZeroexDebug(uint status, uint expectedOutput, uint remaining, uint lastAmount);
    event OutputDebug(uint lastAmount, uint checkMinAmountOut, uint estGasCost);
    event FailCheck(uint targetBlock);
    
    function ExecuteArbitrage(SequenceParams memory Params, Swap[] memory Requests, Zeroex.OrderParams[] memory zeroExOrders) public {
        uint256 gasStart = gasleft();
        bool isValid;
        if (Params.targetBlock == 0 || block.number == Params.targetBlock) {
            isValid = true;
        } else {
            emit FailCheck(Params.targetBlock);
        }
        if (isValid) {
            uint requestsLength = Requests.length;
            uint checkMinAmountOut = Params.initialAmountIn;
            if (Params.WithLoan) {
                if (Params.LoanProtocolID == 1) {
                    ERC20Token tokenIn = ERC20Token(Params.RepayToken);
                    ERC20Token tokenOut = ERC20Token(Params.LoanToken);
                    uint256 balTokenIn = tokenIn.balanceOf(Params.LoanContract);
                    uint256 balTokenOut = tokenOut.balanceOf(Params.LoanContract);
                    checkMinAmountOut = UniswapV2Library.getAmountIn(Params.initialAmountIn, balTokenIn, balTokenOut, Params.swapFees);
                    emit UniswapDebug(Params.LoanContract, balTokenIn, balTokenOut, Params.initialAmountIn, checkMinAmountOut);
                }
            }
            address bancorNetworkAddress;
            IBancorNetwork networkContract;
            bool bancorNetworkContractDefined;
            uint lastAmount = Params.initialAmountIn;
            for (uint i=0; i<requestsLength; i++) {
                Swap memory currentRequest = Requests[i];
                if (currentRequest.protocol == 1) { // Uniswap
                    ERC20Token tokenIn = ERC20Token(currentRequest.tokenAcontract);
                    ERC20Token tokenOut = ERC20Token(currentRequest.tokenBcontract);
                    uint256 balTokenIn = tokenIn.balanceOf(currentRequest.poolContract);
                    uint256 balTokenOut = tokenOut.balanceOf(currentRequest.poolContract);
                    uint inputAmount = lastAmount;
                    lastAmount = UniswapV2Library.getAmountOut(inputAmount, balTokenIn, balTokenOut, currentRequest.swapFees);
                    emit UniswapDebug(currentRequest.poolContract, balTokenIn, balTokenOut, inputAmount, lastAmount);
                } else if (currentRequest.protocol == 2) { // Balancer
                    ERC20Token tokenIn = ERC20Token(currentRequest.tokenAcontract);
                    ERC20Token tokenOut = ERC20Token(currentRequest.tokenBcontract);
                    uint256 balTokenIn = tokenIn.balanceOf(currentRequest.poolContract);
                    uint256 balTokenOut = tokenOut.balanceOf(currentRequest.poolContract);
                    BalancerPool pool = BalancerPool(currentRequest.poolContract);
                    uint dwTokenIn = pool.getDenormalizedWeight(currentRequest.tokenAcontract);
                    uint dwTokenOut = pool.getDenormalizedWeight(currentRequest.tokenBcontract);
                    uint poolFees = pool.getSwapFee();
                    uint inputAmount = lastAmount;
                    lastAmount = bmaths.calcOutGivenIn(balTokenIn, dwTokenIn, balTokenOut, dwTokenOut, inputAmount, poolFees);
                    uint outputAmount = lastAmount;
                    emit BalancerDebug(currentRequest.poolContract, balTokenIn, dwTokenIn, balTokenOut, dwTokenOut, inputAmount, outputAmount);
                } else if (currentRequest.protocol == 4) { // Bancor
                   if (!bancorNetworkContractDefined) {
                        (networkContract, bancorNetworkAddress) = getBancorNetworkContract();
                        bancorNetworkContractDefined = true;
                   }
                   IConverter bancorPool = IConverter(currentRequest.poolContract);
                   address[] memory path = new address[](3);
                   path[0] = currentRequest.tokenAcontract;
                   path[1] = address(bancorPool.anchor());
                   path[2] = currentRequest.tokenBcontract;
                   uint inputAmount = lastAmount;
                   uint fees;
                   (lastAmount, fees) = networkContract.getReturnByPath(path, inputAmount);
                   emit BancorDebug(path, inputAmount, lastAmount);
                } else if (currentRequest.protocol == 3) { // Curve
                    CurveRegistry exchangeContract = CurveRegistry(currentRequest.swapContract);
                    uint inputAmount = lastAmount;
                    lastAmount = exchangeContract.get_exchange_amount(currentRequest.poolContract, currentRequest.tokenAcontract, currentRequest.tokenBcontract, inputAmount);
                    emit CurveDebug(inputAmount, lastAmount);
                } else if (currentRequest.protocol == 5) { // Zeroex
                    Zeroex.OrderParams memory Order = Zeroex.GetOrderByID(zeroExOrders, currentRequest.zeroExOrderID);
                    ZeroexExchange Exchange = ZeroexExchange(currentRequest.swapContract);
                    Zeroex.OrderInfo memory orderInfo = Exchange.getOrderInfo(Order.zOrder);
                    uint Remaining = Order.zOrder.takerAssetAmount - orderInfo.orderTakerAssetFilledAmount;
                    if (orderInfo.orderStatus == uint8(Zeroex.OrderStatus.FILLABLE) && Remaining > lastAmount) {
                        uint inputAmount = lastAmount;
                        lastAmount = Zeroex.CalcOutput(inputAmount, Order.inputDecimals, Order.outputDecimals, Order.price);
                    } else {
                        lastAmount = 1;
                    }
                    emit ZeroexDebug(orderInfo.orderStatus, Order.price, Remaining, lastAmount);
                }
            }
            checkMinAmountOut = checkMinAmountOut + Params.estGasCost;
            if (lastAmount < checkMinAmountOut) {
                isValid = false;
            }
            emit OutputDebug(lastAmount, checkMinAmountOut, Params.estGasCost);
        }
        if (isValid) {
            if (Params.WithLoan) {
                if (Params.LoanProtocolID == 1) {
                    UniswapLoan(Params.LoanContract, Params.LoanToken, Params.RepayToken, Params.initialAmountIn, Params.minAmountOut, Requests, zeroExOrders);
                } else if (Params.LoanProtocolID == 2) {
                    dYdXLoan(Params.LoanContract, Params.LoanToken, Params.RepayToken, Params.initialAmountIn, Params.minAmountOut, Requests, zeroExOrders);
                } else if (Params.LoanProtocolID == 3) {
                    AaveLoan(Params.LoanContract, Params.LoanToken, Params.RepayToken, Params.initialAmountIn, Params.minAmountOut, Requests, zeroExOrders);
                } else {
                    revert();
                }
            } else {
                Sequence(Params.initialAmountIn, Params.minAmountOut, Requests, zeroExOrders);
            }
        }
        if (Params.UseChi) {
            uint256 gasSpent = 31000 + gasStart - gasleft() + 16 * msg.data.length;
            ChiGT Chi = ChiGT(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c);
            Chi.freeUpTo((gasSpent + 14154) / 41130);
        }
    }

    function Sequence(uint initialAmountIn, uint minAmountOut, Swap[] memory swapRequests, Zeroex.OrderParams[] memory zeroExOrders) internal {
        uint requestsLength = swapRequests.length;
        uint lastAmountOut = initialAmountIn;
        for (uint i=0; i<requestsLength; i++) { 
            Swap memory currentRequest = swapRequests[i];
            if (lastAmountOut == 0) {
                revert();
            }
            if (currentRequest.protocol == 1) { // Uniswap
                uint currAmountOut = SwapUniswap(currentRequest.swapContract, currentRequest.tokenAcontract, currentRequest.tokenBcontract, lastAmountOut);
                if (currAmountOut > type(uint).min) {
                    lastAmountOut = currAmountOut;
                } else {
                    revert();
                }
            } else if (currentRequest.protocol == 2) { // Balancer
                uint currAmountOut = SwapBalancer(currentRequest.swapContract, currentRequest.tokenAcontract, currentRequest.tokenBcontract, lastAmountOut);
                if (currAmountOut > type(uint).min) {
                    lastAmountOut = currAmountOut;
                } else {
                    revert();
                }
            } else if (currentRequest.protocol == 3) { // Curve
                uint currAmountOut = SwapCurve(currentRequest.swapContract, currentRequest.poolContract, currentRequest.tokenAcontract, currentRequest.tokenBcontract, lastAmountOut);
                if (currAmountOut > type(uint).min) {
                    lastAmountOut = currAmountOut;
                } else {
                    revert();
                }
            } else if (currentRequest.protocol == 4) { // Bancor
                uint currAmountOut = SwapBancor(currentRequest.poolContract, currentRequest.tokenAcontract, currentRequest.tokenBcontract, lastAmountOut);
                if (currAmountOut > type(uint).min) {
                    lastAmountOut = currAmountOut;
                } else {
                    revert();
                }
            } else if (currentRequest.protocol == 5) { // 0x
                Zeroex.OrderParams memory Order = Zeroex.GetOrderByID(zeroExOrders, currentRequest.zeroExOrderID);
                uint currAmountOut = SwapZeroex(currentRequest.swapContract, currentRequest.poolContract, currentRequest.tokenAcontract, lastAmountOut, Order);
                if (currAmountOut > type(uint).min) {
                    lastAmountOut = currAmountOut;
                } else {
                    revert();
                }
            } else {
                revert();
            }
        }
        if (lastAmountOut < minAmountOut) {
            revert();
        }
    }
    
    function SwapZeroex(address swapContract, address poolContract, address contractA, uint amountIn, Zeroex.OrderParams memory order) public returns(uint) {
        checkAllowance(contractA, swapContract, amountIn);
        checkAllowance(contractA, poolContract, amountIn);
        ZeroexExchange Exchange = ZeroexExchange(swapContract);
        Zeroex.FillResults memory results = Exchange.fillOrder(order.zOrder, amountIn, order.signature);
        return results.makerAssetFilledAmount;
    }
    
    function SwapUniswap(address swapContract, address contractA, address contractB, uint amountIn) public returns(uint) {
        checkAllowance(contractA, swapContract, amountIn);
        UniswapRouter router = UniswapRouter(swapContract);
        address[] memory fullPath = new address[](2);
        fullPath[0] = contractA;
        fullPath[1] = contractB;
        uint deadLine = block.timestamp + 20 minutes;
        uint[] memory Output = router.swapExactTokensForTokens(amountIn, type(uint).min, fullPath, address(this), deadLine);
        return Output[Output.length - 1];
    }
    
    function SwapBalancer(address swapContract, address contractA, address contractB, uint amountIn) public returns(uint) {
        checkAllowance(contractA, swapContract, amountIn);
        BalancerPool pool = BalancerPool(swapContract);
        (uint tokenOut, uint spotAfter) = pool.swapExactAmountIn(contractA, amountIn, contractB, type(uint).min, type(uint).max);
        return tokenOut;
    }
    
    function SwapBancor(address swapContract, address contractA, address contractB, uint amountIn) public returns(uint) {
        address ethBancorAddress = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
        (IBancorNetwork networkContract, address bancorNetworkAddress) = getBancorNetworkContract();
        IConverter bancorPool = IConverter(swapContract);
        address[] memory path = new address[](3);
        path[0] = contractA;
        path[1] = address(bancorPool.anchor());
        path[2] = contractB;
        uint ethInputAmount;
        if (contractA != ethBancorAddress) {
            checkAllowance(contractA, bancorNetworkAddress, amountIn);
        } else {
            // WETH -> ETH
            IERC20 wETH = IERC20(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2); // IERC20(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
            wETH.withdraw(amountIn);
            ethInputAmount = amountIn;
        }
        uint tokenOut = networkContract.convert{value: ethInputAmount}(path, amountIn, type(uint).min+1);
        if (contractB == ethBancorAddress) {
            // ETH -> WETH
            address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2).call{value: tokenOut}("");
        }
        return tokenOut;
    }
    
    function SwapCurve(address swapContract, address poolContract, address contractA, address contractB, uint amountIn) public returns(uint) {
        checkAllowance(contractA, swapContract, amountIn);
        CurveRegistry registry = CurveRegistry(swapContract);
        uint tokenOut = registry.exchange(poolContract, contractA, contractB, amountIn, type(uint).min);
        return tokenOut;
    }
    
    function checkAllowance(address tokenContract, address spenderContract, uint256 approvedAmount) internal {
        ERC20Token targetToken = ERC20Token(tokenContract);
        uint256 allowed = targetToken.allowance(address(this), spenderContract);
        if (allowed < approvedAmount) {
            if (allowed != 0) {
                safeApprove(IERC20(tokenContract), spenderContract, 0);
            }
            safeApprove(IERC20(tokenContract), spenderContract, (approvedAmount * 1000));
        }
    }
    
    function safeApprove(IERC20 token, address spender, uint256 value) internal {
        _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, value));
    }
    
    function _callOptionalReturn(IERC20 token, bytes memory data) private {
        bytes memory returndata = address(token).functionCall(data, "SafeERC20: low-level call failed");
        if (returndata.length > 0) {
            require(abi.decode(returndata, (bool)));
        }
    }
    
    // Uniswap flash loan
    
    struct unilikeParams {
        uint feesA;
        uint feesB;
        address sender;
        uint amount0;
        uint amount1;
        bytes data;
    }
    
    function unilikeCall(unilikeParams memory params) internal {
        require(params.sender == address(this)); // ensure that proxy contract is the caller
        address token0 = IUniswapV2Pair(msg.sender).token0(); 
        address token1 = IUniswapV2Pair(msg.sender).token1();
        
        (
            address LoanContract,
            address LoanToken,
            address RepayToken,
            uint initialAmountIn,
            uint minAmountOut,
            Swap[] memory swapRequests,
            Zeroex.OrderParams[] memory zeroExOrders
        ) = abi.decode(params.data, (address, address, address, uint, uint, Swap[], Zeroex.OrderParams[]));
        require(msg.sender == LoanContract); // ensure that msg.sender is LoanContract
    
        uint amountToRepay;
        uint ReserveIn;
        uint ReserveOut;
        if (LoanToken == RepayToken) {
            uint fee = ((initialAmountIn * params.feesA) / params.feesB) + 1;
            amountToRepay = initialAmountIn + fee;
        } else {
            (uint Reserve0, uint Reserve1, uint BlockTimestamp) = IUniswapV2Pair(msg.sender).getReserves();
            if (LoanToken == token0) {
                ReserveIn = Reserve1;
                ReserveOut = Reserve0;
            }
            if (LoanToken == token1) {
                ReserveIn = Reserve0;
                ReserveOut = Reserve1;
            }
            amountToRepay = UniswapV2Library.getAmountIn(initialAmountIn, ReserveIn, ReserveOut, 3);
        }
        
        Sequence(initialAmountIn, amountToRepay, swapRequests, zeroExOrders);
        
        ERC20Token(RepayToken).transfer(msg.sender, amountToRepay);
    }
    
    function uniswapV2Call(address sender, uint amount0, uint amount1, bytes calldata data) external {
        unilikeParams memory params;
        params.feesA = 3;
        params.feesB = 997;
        params.sender = sender;
        params.amount0 = amount0;
        params.amount1 = amount1;
        params.data = data;
        unilikeCall(params);
    }
    
    function pancakeCall(address sender, uint amount0, uint amount1, bytes calldata data) external {
        unilikeParams memory params;
        params.feesA = 2;
        params.feesB = 998;
        params.sender = sender;
        params.amount0 = amount0;
        params.amount1 = amount1;
        params.data = data;
        unilikeCall(params);
    }
    
    function demaxCall(address sender, uint amount0, uint amount1, bytes calldata data) external {
        unilikeParams memory params;
        params.feesA = 3;
        params.feesB = 997;
        params.sender = sender;
        params.amount0 = amount0;
        params.amount1 = amount1;
        params.data = data;
        unilikeCall(params);
    }
    
    function BSCswapCall(address sender, uint amount0, uint amount1, bytes calldata data) external {
        unilikeParams memory params;
        params.feesA = 3;
        params.feesB = 997;
        params.sender = sender;
        params.amount0 = amount0;
        params.amount1 = amount1;
        params.data = data;
        unilikeCall(params);
    }
    
    function ThugswapCall(address sender, uint amount0, uint amount1, bytes calldata data) external {
        unilikeParams memory params;
        params.feesA = 4;
        params.feesB = 996;
        params.sender = sender;
        params.amount0 = amount0;
        params.amount1 = amount1;
        params.data = data;
        unilikeCall(params);
    }
    
    function croDefiSwapCall(address sender, uint amount0, uint amount1, bytes calldata data) external {
        unilikeParams memory params;
        params.feesA = 3;
        params.feesB = 997;
        params.sender = sender;
        params.amount0 = amount0;
        params.amount1 = amount1;
        params.data = data;
        unilikeCall(params);
    }
    
    function UniswapLoan(address LoanContract, address LoanToken, address RepayToken, uint initialAmountIn, uint minAmountOut, Swap[] memory swapRequests, Zeroex.OrderParams[] memory zeroExOrders) internal {
        address token0 = IUniswapV2Pair(LoanContract).token0(); 
        address token1 = IUniswapV2Pair(LoanContract).token1();
        
        uint amount0Out;
        uint amount1Out;
        if (LoanToken == token0) {
            amount0Out = initialAmountIn;
            amount1Out = 0;
        } else if (LoanToken == token1) {
            amount1Out = initialAmountIn;
            amount0Out = 0;
        }
        require(amount1Out == 0 || amount0Out == 0);
        
        Proxy(address(this)).setAllowedDelegate(LoanContract);
        
        bytes memory SequenceData = abi.encode(
            LoanContract,
            LoanToken,
            RepayToken,
            initialAmountIn,
            minAmountOut,
            swapRequests,
            zeroExOrders
        );
        IUniswapV2Pair(LoanContract).swap(amount0Out, amount1Out, address(this), SequenceData);
    }
    
    // dYdX flash loan
    
    function callFunction(address sender, Account.Info memory account, bytes memory data) external {
        (
            address LoanContract,
            address LoanToken,
            address RepayToken,
            uint initialAmountIn,
            uint minAmountOut,
            Swap[] memory swapRequests,
            Zeroex.OrderParams[] memory zeroExOrders
        ) = abi.decode(data, (address, address, address, uint, uint, Swap[], Zeroex.OrderParams[]));
        
        Sequence(initialAmountIn, minAmountOut, swapRequests, zeroExOrders);
    }
    
    function dYdXLoan(address LoanContract, address LoanToken, address RepayToken, uint initialAmountIn, uint minAmountOut, Swap[] memory swapRequests, Zeroex.OrderParams[] memory zeroExOrders) internal {
        bytes memory SequenceData = abi.encode(
            LoanContract,
            LoanToken,
            RepayToken,
            initialAmountIn,
            minAmountOut,
            swapRequests,
            zeroExOrders
        );
        uint RepayAmount = initialAmountIn.add(2);
        ISoloMargin soloContract = ISoloMargin(LoanContract);
        uint256 marketId = DydxFlashloanBase._getMarketIdFromTokenAddress(LoanContract, LoanToken);
        checkAllowance(LoanToken, LoanContract, RepayAmount);
        
        Actions.ActionArgs[] memory operations = new Actions.ActionArgs[](3);
        
        operations[0] = DydxFlashloanBase._getWithdrawAction(marketId, initialAmountIn);
        operations[1] = DydxFlashloanBase._getCallAction(SequenceData);
        operations[2] = DydxFlashloanBase._getDepositAction(marketId, RepayAmount);
        
        Account.Info[] memory accountInfos = new Account.Info[](1);
        accountInfos[0] = DydxFlashloanBase._getAccountInfo();
        
        Proxy(address(this)).setAllowedDelegate(LoanContract);
        soloContract.operate(accountInfos, operations);
    }
    
    // Aave flash loan
    
    function executeOperation(address _reserve, uint256 _amount, uint256 _fee, bytes memory _params) external {
        (
            address LoanContract,
            address LoanToken,
            address RepayToken,
            uint initialAmountIn,
            uint minAmountOut,
            Swap[] memory swapRequests,
            Zeroex.OrderParams[] memory zeroExOrders
        ) = abi.decode(_params, (address, address, address, uint, uint, Swap[], Zeroex.OrderParams[]));
        
        uint RepayAmount = _amount.add(_fee);
        
        Sequence(initialAmountIn, minAmountOut, swapRequests, zeroExOrders);
        
        address Provider = ILendingPool(LoanContract).addressesProvider();
        address payable LendingCore = ILendingPoolAddressesProvider(Provider).getLendingPoolCore();
        
        ERC20Token(LoanToken).transfer(LendingCore, RepayAmount);
    }
    
    function AaveLoan(address LoanContract, address LoanToken, address RepayToken, uint initialAmountIn, uint minAmountOut, Swap[] memory swapRequests, Zeroex.OrderParams[] memory zeroExOrders) internal {
        bytes memory SequenceData = abi.encode(
            LoanContract,
            LoanToken,
            RepayToken,
            initialAmountIn,
            minAmountOut,
            swapRequests,
            zeroExOrders
        );
        ILendingPool LendingPool = ILendingPool(LoanContract);
        Proxy(address(this)).setAllowedDelegate(LoanContract);
        LendingPool.flashLoan(address(this), LoanToken, initialAmountIn, SequenceData);
    }
    
    function getBancorNetworkContract() public returns(IBancorNetwork, address) {
        IContractRegistry contractRegistry = IContractRegistry(0x52Ae12ABe5D8BD778BD5397F99cA900624CfADD4); // IContractRegistry(0x52Ae12ABe5D8BD778BD5397F99cA900624CfADD4);
        address output = contractRegistry.addressOf('BancorNetwork');
        return (IBancorNetwork(output), output);
    }
    
}

