import "./SafeMath.sol";

interface ZeroexExchange {
  function fillOrder(Zeroex.Order memory order, uint256 takerAssetFillAmount, bytes memory signature) external payable returns (Zeroex.FillResults memory fillResults);
  function getOrderInfo(Zeroex.Order memory order) external view returns (Zeroex.OrderInfo memory orderInfo);
}

library Zeroex {
    enum OrderStatus {
        INVALID,                     // Default value
        INVALID_MAKER_ASSET_AMOUNT,  // Order does not have a valid maker asset amount
        INVALID_TAKER_ASSET_AMOUNT,  // Order does not have a valid taker asset amount
        FILLABLE,                    // Order is fillable
        EXPIRED,                     // Order has already expired
        FULLY_FILLED,                // Order is fully filled
        CANCELLED                    // Order has been cancelled
    }

    struct OrderParams {
        Order zOrder;
        bytes signature;
        uint price;
        uint inputDecimals;
        uint outputDecimals;
        uint ID;
    }

    struct Order {
        address makerAddress;           // Address that created the order.
        address takerAddress;           // Address that is allowed to fill the order. If set to 0, any address is allowed to fill the order.
        address feeRecipientAddress;    // Address that will recieve fees when order is filled.
        address senderAddress;          // Address that is allowed to call Exchange contract methods that affect this order. If set to 0, any address is allowed to call these methods.
        uint256 makerAssetAmount;       // Amount of makerAsset being offered by maker. Must be greater than 0.
        uint256 takerAssetAmount;       // Amount of takerAsset being bid on by maker. Must be greater than 0.
        uint256 makerFee;               // Fee paid to feeRecipient by maker when order is filled.
        uint256 takerFee;               // Fee paid to feeRecipient by taker when order is filled.
        uint256 expirationTimeSeconds;  // Timestamp in seconds at which order expires.
        uint256 salt;                   // Arbitrary number to facilitate uniqueness of the order's hash.
        bytes makerAssetData;           // Encoded data that can be decoded by a specified proxy contract when transferring makerAsset. The leading bytes4 references the id of the asset proxy.
        bytes takerAssetData;           // Encoded data that can be decoded by a specified proxy contract when transferring takerAsset. The leading bytes4 references the id of the asset proxy.
        bytes makerFeeAssetData;        // Encoded data that can be decoded by a specified proxy contract when transferring makerFeeAsset. The leading bytes4 references the id of the asset proxy.
        bytes takerFeeAssetData;        // Encoded data that can be decoded by a specified proxy contract when transferring takerFeeAsset. The leading bytes4 references the id of the asset proxy.
    }

    struct FillResults {
        uint256 makerAssetFilledAmount;  // Total amount of makerAsset(s) filled.
        uint256 takerAssetFilledAmount;  // Total amount of takerAsset(s) filled.
        uint256 makerFeePaid;            // Total amount of fees paid by maker(s) to feeRecipient(s).
        uint256 takerFeePaid;            // Total amount of fees paid by taker to feeRecipients(s).
        uint256 protocolFeePaid;         // Total amount of fees paid by taker to the staking contract.
    }

    struct OrderInfo {
        uint8 orderStatus;                    // Status that describes order's validity and fillability.
        bytes32 orderHash;                    // EIP712 typed data hash of the order (see LibOrder.getTypedDataHash).
        uint256 orderTakerAssetFilledAmount;  // Amount of order that has already been filled.
    }

    function CalcOutput(uint inputAmount, uint inputDecimals, uint outputDecimals, uint price) internal pure returns(uint outputAmount) {
        uint a = SafeMath.mul(inputAmount, price);
        uint b = SafeMath.mul((10**18), inputDecimals);
        uint c = SafeMath.div(b, outputDecimals);
        outputAmount = SafeMath.div(a, c);
        return outputAmount;
    }

    function GetOrderByID(OrderParams[] memory zeroExOrders, uint ID) internal pure returns(OrderParams memory result) {
        uint ordersLength = zeroExOrders.length;
        for (uint i=0; i<ordersLength; i++) {
            OrderParams memory currentOrder = zeroExOrders[i];
            if (currentOrder.ID == ID) {
                result = currentOrder;
                break;
            }
        }
        return result;
    }
}
