contracts/Auction.sol.content:
pragma solidity ^0.8.0;

import "./helpers/Ownable.sol";
import "./helpers/ECDSA.sol";
import "./helpers/SafeMath.sol";

contract Auction is Ownable {
    using ECDSA for bytes32;
    using SafeMath for uint256;
    enum Status {Null, Reserved, Canceled, Claimed}

    struct State {
        Status status;
        uint256 amount;
    }

    uint256 public feep;
    address constant hotwallet = address(0xf2eC2fE12EDfeb03e6241bFC8A8E918B63da4F93);

    uint8 constant b = 0;
    uint8 constant claim = 1;
    uint8 constant cancel = 2;
    uint8 constant up = 3;

    event Bidded(uint64 indexed bidID);
    event BidCanceled(uint64 indexed bidID);
    event BidClaimed(uint64 indexed bidID);
    event BidUpped(uint64 indexed bidID);

    mapping(uint256 => State) bids;

    constructor(uint256 _feep) public {
        feep = _feep;
    }

    function isSigned(
        uint8 _prefix,
        uint64 _bidID,
        uint256 _amount,
        bytes32 _r,
        bytes32 _s,
        uint8 _v
    ) private view returns (bool){
        bytes32 _hash = keccak256(abi.encodePacked(_prefix, _bidID, _amount, msg.sender));
        address _signer = ecrecover(_hash, _v, _r, _s);
        return _signer == hotwallet;
    }

    function makeBid(
        uint256 _timestamp,
        uint64 _bidID,
        bytes32 _r,
        bytes32 _s,
        uint8 _v
    ) external payable {
        require(_timestamp >= block.timestamp, "bid-expired");
        State storage bid = bids[_bidID];
        require(bid.status == Status.Null, "bid-reserved");
        require(isSigned(b, _bidID, msg.value, _r, _s, _v), "bad-signature");

        bid.status = Status.Reserved;
        bid.amount = msg.value;

        emit Bidded(_bidID);
    }

    function upBid(
        uint256 _timestamp,
        uint64 _upID,
        uint64 _bidID,
        bytes32 _r,
        bytes32 _s,
        uint8 _v
    ) external payable {
        require(_timestamp >= block.timestamp, "up-bid-expired");
        State storage bid = bids[_bidID];
        require(bid.status == Status.Reserved, "bid-reserved");
        require(isSigned(up, _bidID, msg.value, _r, _s, _v), "bad-signature");

        bid.amount = bid.amount.add(msg.value);

        emit BidUpped(_upID);
    }


    function cancelBid(
        uint64 _bidID,
        bytes32 _r,
        bytes32 _s,
        uint8 _v
    ) external payable {
        State storage bid = bids[_bidID];
        require(bid.status == Status.Reserved, "bid-is-not-reserved");
        require(isSigned(cancel, _bidID, 0, _r, _s, _v), "bad-signature");

        bid.status = Status.Canceled;
        emit BidCanceled(_bidID);

        (bool success,) = msg.sender.call{ value : bid.amount}("");
        require(success, "bad-contract-balance");
    }


    function claimBid(
        uint64 _bidID,
        bytes32 _r,
        bytes32 _s,
        uint8 _v
    ) external payable {
        State storage bid = bids[_bidID];
        require(bid.status == Status.Reserved, "bid-is-not-reserved");
        require(isSigned(claim, _bidID, 0, _r, _s, _v), "bad-signature");

        bid.status = Status.Claimed;
        emit BidClaimed(_bidID);

        uint256 _f = bid.amount.div(100).mul(feep);

        (bool success,) = msg.sender.call{value : bid.amount.sub(_f)}("");
        require(success, "bad-contract-balance");

        (success,) = hotwallet.call{value : _f}("");
        require(success, "bad-contract-balance");
    }

    function setFee(uint256 _feep) external onlyOwner {
        require(_feep < 100,"bad-fee");
        feep = _feep;
    }
}

