pragma solidity >=0.7.4;

contract DATA_CHANGE {
    
    mapping (address => uint256) public balanceOf;

    struct SYSTEM{
        uint64 StarAngel;// 星使总人数
        uint64 StarLeval;// 层数
        uint256 maxAirDrop;// 空投最大值
        uint256 alreadyBuy;
        uint256 ObliterateTime;
    }
    struct USER{
        uint64 id;
        uint64 referee;// 上级
        uint64 grade;//星使等级 
        bool Airdrop;// 是否已领取 过空投
        uint256 totalInputEth;
    }
     mapping(address =>USER)public StarAngels;// 星使
    mapping(uint64 =>address)public StarAngelID;// 星使id
    SYSTEM public sys;
    DATA_CHANGE old_data=DATA_CHANGE(0x18E45D8C11416FC9079f74A3d766388Ef3D1ACA1);
    DATA_CHANGE new_data=DATA_CHANGE(0xd0041bfe0999be8d88bae4aBBC71a5925FDCF950);
    uint256 public totalSupply;
    constructor () public{

    }

    function setSystem(uint64 angel,uint64 leval,uint256 max,uint256 alBuy,uint256 obl,uint256 totalVeis)public {
        sys.StarAngel=angel;
        sys.StarLeval=leval;
        sys.maxAirDrop=max;
        sys.alreadyBuy=alBuy;
        sys.ObliterateTime = obl;
        totalSupply = totalVeis;
    }
    function setUser(address addr,uint64 id,uint64 referee,uint64 grade,bool airdrop,uint256 totaleth,uint256 balan)public {
        USER storage u=StarAngels[addr];
        u.id=id;
        u.referee=referee;
        u.grade=grade;
        u.Airdrop=airdrop;
        u.totalInputEth=totaleth;
        if(id>0)StarAngelID[id]=addr;
        if(balan >0)balanceOf[addr]=balan;
    }
    
    function get_Old_sys()public{
        SYSTEM memory u;
        (u.StarAngel,,u.maxAirDrop,,)=old_data.sys();
        uint256 t=old_data.totalSupply();
        u.StarLeval = 48;
        new_data.setSystem(u.StarAngel,u.StarLeval,u.maxAirDrop,u.alreadyBuy,u.ObliterateTime,t);
    }

}
