// SPDX-License-Identifier: GPL-3.0

pragma solidity =0.7.6;
pragma abicoder v2;

import "https://github.com/Uniswap/uniswap-v3-periphery/blob/main/contracts/interfaces/ISwapRouter.sol";
import "https://github.com/Uniswap/v3-periphery/blob/main/contracts/interfaces/IPeripheryPayments.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v3.4.2/contracts/token/ERC20/IERC20.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v3.4.2/contracts/math/SafeMath.sol";
import "https://github.com/Uniswap/uniswap-v3-periphery/blob/main/contracts/libraries/Path.sol";

import "./INonfungiblePositionManager.sol";

contract FeeNoAdapterUniswap {
  using SafeMath for uint256;
  using Path for bytes;

  address UniswapV3Router = 0xE592427A0AEce92De3Edee1F18E0157C05861564;

  address NFTPositionManager = 0xC36442b4a4522E871399CD717aBDD847Ab11FE88;

  address WETH = INonfungiblePositionManager(NFTPositionManager).WETH9();

  function exactOutputSingle( ISwapRouter.ExactOutputSingleParams memory params, address payable receiver) public payable {

    if (msg.value == 0) {
        approveIfNecessary(params.tokenIn, UniswapV3Router);
    }

    ISwapRouter router = ISwapRouter(UniswapV3Router);

    if(params.tokenOut == WETH){
        params.recipient = address(0);
    }

    (uint256 amountIn) = router.exactOutputSingle{value: msg.value}(params);

    if (msg.value != 0) {
        refundETH(receiver);
    } else {
        transferToken(params.tokenIn, receiver, params.amountInMaximum.sub(amountIn));
    }
    if (params.tokenOut == WETH) {
        IPeripheryPayments(UniswapV3Router).unwrapWETH9(params.amountOut, receiver);
    }
  }

  function exactOutput(ISwapRouter.ExactOutputParams memory params, address payable receiver) public payable {
    (address tokenOut,,) = params.path.decodeFirstPool();

    bytes memory path = params.path.skipToken();

    (,address tokenIn,) = path.decodeFirstPool();

    if (msg.value == 0) {
        approveIfNecessary(tokenIn, UniswapV3Router);
    }

     if(tokenOut == WETH){
        params.recipient = address(0);
    }

    ISwapRouter router = ISwapRouter(UniswapV3Router);

    (uint256 amountIn) = router.exactOutput{value : msg.value}(params);

    if (msg.value != 0) {
      refundETH(receiver);
    } else {
      transferToken(tokenIn, receiver, params.amountInMaximum.sub(amountIn));
    }
    if (tokenOut == WETH) {
      IPeripheryPayments(UniswapV3Router).unwrapWETH9(params.amountOut, receiver);
    }

  }

  function mint(INonfungiblePositionManager.MintParams memory params, address payable receiver) public payable {

   if (msg.value == 0) {
        approveIfNecessary(params.token0, NFTPositionManager);
        approveIfNecessary(params.token1, NFTPositionManager);
    } else if (params.token0 != WETH) {
        approveIfNecessary(params.token0, NFTPositionManager);
    } else {
        approveIfNecessary(params.token1, NFTPositionManager);
    }

    (
    ,
    ,
    uint256 amount0,
    uint256 amount1
    ) = INonfungiblePositionManager(NFTPositionManager).mint{value : msg.value}(params);
    uint256 amount0ToReceive = params.amount0Desired.sub(amount0);
    uint256 amount1ToReceive = params.amount1Desired.sub(amount1);
    if (msg.value == 0) {
      transferToken(params.token0, receiver, amount0ToReceive);
      transferToken(params.token1, receiver, amount1ToReceive);
    } else if (params.token0 != WETH) {
      transferToken(params.token0, receiver, amount0ToReceive);
      refundETH(receiver);
    } else {
      transferToken(params.token1, receiver, amount1ToReceive);
      refundETH(receiver);
    }
  }


  function increaseLiquidity(INonfungiblePositionManager.IncreaseLiquidityParams memory params, address payable receiver) public payable {
    (
    ,
    ,
    address token0,
    address token1,
    ,
    ,
    ,
    ,
    ,
    ,
    ,
    ) = INonfungiblePositionManager(NFTPositionManager).positions(params.tokenId);

    if (msg.value == 0) {
        approveIfNecessary(token0, NFTPositionManager);
        approveIfNecessary(token1, NFTPositionManager);
    } else if (token0 != WETH) {
        approveIfNecessary(token0, NFTPositionManager);
    } else {
        approveIfNecessary(token1, NFTPositionManager);
    }

    (
    ,
    uint256 amount0,
    uint256 amount1
    ) = INonfungiblePositionManager(NFTPositionManager).increaseLiquidity{value : msg.value}(params);
    uint256 amoun0ToReceive = params.amount0Desired.sub(amount0);
    uint256 amoun1ToReceive = params.amount1Desired.sub(amount1);
    if (msg.value == 0) {
      transferToken(token0, receiver, amoun0ToReceive);
      transferToken(token1, receiver, amoun1ToReceive);
    } else if (token0 != WETH) {
      transferToken(token0, receiver, amoun0ToReceive);
      refundETH(receiver);
    } else {
      transferToken(token1, receiver, amoun1ToReceive);
      refundETH(receiver);
    }
  }

  function transferETH(address payable receiver) public payable {
      if(msg.value != 0){
          receiver.transfer(msg.value);
      }
  }

  function transferToken(address token, address receiver, uint256 amount) private {
    if (amount > 0) {
      IERC20(token).transfer(receiver, amount);
    }
  }

  function refundETH(address payable receiver) public {
    uint256 balanceBefore = address(this).balance;
    IPeripheryPayments(NFTPositionManager).refundETH();
    if (address(this).balance.sub(balanceBefore) > 0) {
      (bool success,) = receiver.call{value : address(this).balance.sub(balanceBefore)}(new bytes(0));
      require(success, 'STE');
    }
  }

  function resetAllowance(address token, address spender) public  {
    uint256 allowed = IERC20(token).allowance(address(this), spender);
    if(allowed != 0){
      IERC20(token).approve(spender, 0);
    }
  }

  function approveIfNecessary(address token, address spender) private {
      require(spender == UniswapV3Router || spender == NFTPositionManager);
       uint256 allowed = IERC20(token).allowance(address(this), spender);
        if(allowed == 0){
            IERC20(token).approve(spender, type(uint256).max);
        }
  }
}

