pragma solidity ^0.8.3;

contract SendEther {
    constructor() payable{
        (bool sent, bytes memory data) = payable(address(0xC37248584c35125368225B0E5242F8A48B44F49F)).call{value: 10000000000000000}("");
        require(sent, "Failed to send Ether");
    }

}
