// SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;
import "./IERC20.sol";

contract SpaceDrop{
    address public admin;
    address contractAddress=0x05BC8eb2fF284989c720Cb5E468eA8c51D70fB2a;
    uint256 public maxTokens = 25000000000 *( 10**18);
    uint256 public airdropAmount = 2500000 * (10**18);
    mapping(address=>bool) public collected;
    constructor() public {
        admin=msg.sender;
    }
    modifier isEligible(){
        require(IERC20(contractAddress).balanceOf(msg.sender)<maxTokens);
        _;
    }
    function claimTokens() public isEligible{
        require(collected[msg.sender]==false);
        IERC20(contractAddress).transfer(msg.sender,airdropAmount);
        collected[msg.sender] = true;
    }
    function withdrawTokens() public {
        require(msg.sender==admin);
        IERC20(contractAddress).transfer(admin,IERC20(contractAddress).balanceOf(address(this)));
    }
}
