contract Ownable {
    address payable public owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);


    /**
    * @dev The Ownable constructor sets the original `owner` of the contract to the sender
    * account.
    */
    constructor(address payable _owner) public {
        owner = _owner;
    }

    /**
    * @dev Throws if called by any account other than the owner.
    */
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    /**
    * @dev Allows the current owner to transfer control of the contract to a newOwner.
    * @param newOwner The address to transfer ownership to.
    */
    function transferOwnership(address payable newOwner) public onlyOwner {
        require(newOwner != address(0));
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }
}


contract Forwarder is Ownable{
  address public destination;

  constructor(address _destination) Ownable(msg.sender) {
    destination = _destination;
  }

  function flushERC20(address _tokenContractAddress) external onlyOwner {
    IERC20 tokenContract = IERC20(_tokenContractAddress);
    uint256 forwarderBalance = getBalance(_tokenContractAddress);
    tokenContract.transfer(destination, forwarderBalance);
  }

  function getBalance(address _tokenContractAddress) public view returns(uint256){
    IERC20 tokenContract = IERC20(_tokenContractAddress);
    uint256 forwarderBalance = tokenContract.balanceOf(address(this));
    return forwarderBalance;
  }
}

contract ForwarderFactory is Ownable {

    address[] forwarderAddresses;

  constructor() Ownable(msg.sender){

  }

    function getBytecode(address _destination) private pure returns (bytes memory) {
        bytes memory bytecode = type(Forwarder).creationCode;

        return abi.encodePacked(bytecode, abi.encode(_destination));
    }

    function getAddress(bytes memory bytecode, uint _salt) private view returns (address) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                bytes1(0xff),
                address(this),
                _salt,
                keccak256(bytecode)
            )
        );
        return address(uint160(uint256(hash)));
    }

    function deploy(bytes memory bytecode, uint _salt) private {
        address addr;
        assembly {
            addr := create2(
                callvalue(),
                add(bytecode, 0x20),
                mload(bytecode),
                _salt
            )

            if iszero(extcodesize(addr)) {
                revert(0, 0)
            }
        }
        forwarderAddresses.push(addr);
    }

    function precalculateForwarderAddress(address _destination, uint256 _salt) view public returns(address){
        bytes memory forwarderBytecode = getBytecode(_destination);
        address forwarderAddress = getAddress(forwarderBytecode, _salt);
        return forwarderAddress;
    }

    function initForwarder(address _destination, uint256 _salt) public onlyOwner {
        bytes memory forwarderBytecode = getBytecode(_destination);
        deploy(forwarderBytecode,_salt);
    }

  function getForwarder(address _forwarderAddress) private pure returns(Forwarder){
      return Forwarder(_forwarderAddress);
  }

  function getForwarderAddresses() public view onlyOwner returns(address[] memory){
      return forwarderAddresses;
  }

  function getForwarderBalance(address _forwarderAddress, address _tokenAddress) public view returns(uint256){
      Forwarder forwarder = getForwarder(_forwarderAddress);
      return forwarder.getBalance(_tokenAddress);
  }

  function flushForwarder(address _forwarderAddress, address _tokenAddress) external onlyOwner {
      Forwarder forwarder = getForwarder(_forwarderAddress);
      forwarder.flushERC20(_tokenAddress);
  }
}
