contract VolumeMiner is Ownable {
    using SafeMath for uint;
    using SafeERC20 for IERC20;

    address public executor;
    IUniswapV2Router02 public router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
    IERC20 public alpha = IERC20(0xa1faa113cbE53436Df28FF0aEe54275c13B40975);
    IERC20 public ibETH = IERC20(0x67B66C99D3Eb37Fa76Aa3Ed1ff33E8e39F0b9c7A);

    uint public interval = 50 minutes;
    uint public maxSpending = 10000 ether;
    uint public lastCall = 0;

    constructor(address _executor) public {
        executor = _executor;
        alpha.approve(address(router), uint(-1));
        ibETH.approve(address(router), uint(-1));
    }

    function setExecutor(address _executor) public onlyOwner {
        executor = _executor;
    }

    function setInterval(uint _interval) public onlyOwner {
        interval = _interval;
    }

    function setMaxSpending(uint _maxSpending) public onlyOwner {
        maxSpending = _maxSpending;
    }

    function takeMeOutAll(address token) public onlyOwner {
        uint amount = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function takeMeOut(address token, uint amount) public onlyOwner {
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function pumpIt(uint amount, uint times) public {
        require(msg.sender == executor || msg.sender == owner(), 'not executor or owner');
        require(now >= lastCall + interval, 'too soon');
        lastCall = now;
        uint balBefore = alpha.balanceOf(address(this));
        for (uint idx = 0; idx < times; idx ++) {
            address[] memory path = new address[](2);
            path[0] = address(alpha);
            path[1] = address(ibETH);
            router.swapExactTokensForTokens(amount, 0, path, address(this), now + 10);
            path[0] = address(ibETH);
            path[1] = address(alpha);
            router.swapExactTokensForTokens(ibETH.balanceOf(address(this)).sub(1), 0, path, address(this), now + 10);
        }
        uint balAfter = alpha.balanceOf(address(this));
        require(balAfter.sub(balBefore) <= maxSpending, 'spend too much');
    }
}
