pragma solidity ^0.6.6;

contract Interface{

    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline) external payable {}
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external {}
    
}

contract TokenInterface{

    function balanceOf(address account) public view returns (uint256) {}

}

contract SmartUni{

    address payable public owner;
    address public router = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    address public token = 0x0Ae055097C6d159879521C384F1D2123D1f195e6;
    address public weth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    uint public amountOutMin = 1;
    address[] public path = [weth, token];
    address[] public path2 = [token, weth];
    address public to = address(this);
    uint public deadline = 6969696969;

    constructor() payable public {
        owner = msg.sender;
    }

//admin stuff

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function changeTokenAddy(address _newToken) external onlyOwner() {
        token = _newToken;
    }

    function thisBalance() public view returns(uint256){
        return(address(this).balance);
    }    
    
    function thisTokenBalance() public view returns(uint256){
        TokenInterface clone = TokenInterface(token);
        return(clone.balanceOf(address(this)));
    }

    function withdraw() public onlyOwner{
        owner.transfer(address(this).balance);
    }

    fallback() payable external{
    }

//for use

    function uniBuy() public payable onlyOwner {
        Interface clone = Interface(router);
        clone.swapExactETHForTokens.value(address(this).balance)(amountOutMin,path,to,deadline);
    }


    function uniSell() public onlyOwner {
        Interface clone = Interface(router);
        clone.swapExactTokensForETH(thisTokenBalance(),amountOutMin,path2,to,deadline);
    }
}
