pragma solidity >=0.4.25 <0.7.0;

import "./Libraries/math/SafeMath.sol";
import "./Libraries/utils/Ownable.sol";

interface DF9T {
    function settle(uint256 _jackpot, uint256[] memory _priceList) external;
    function redeem(address account, uint256 level, uint256 id, uint256 dividend) external;
}

contract Oracle is Ownable {
    using SafeMath for uint256;

    constructor() public {}

    function settleMarket(DF9T market, uint256 _jackpot, uint256[] memory _priceList) public onlyOwner {
        market.settle(_jackpot, _priceList);
    }
}
