contract Runner {
    address public target;

    function run(
        bytes memory _code
    )
        public
        returns (
            bytes memory
        )
    {
        bytes memory ret;
        address addr;
        assembly {
            addr := create(0, add(_code, 0x20), mload(_code))
        }

        target = addr;
    }

    function result()
        public
        view
        returns (
            bytes memory
        )
    {
        bytes memory ret;
        address addr = target;
        assembly {
            let size := extcodesize(addr)
            ret := mload(0x40)
            mstore(0x40, add(size, add(ret, 0x20)))
            mstore(ret, size)
            extcodecopy(addr, add(ret, 0x20), 0, size)
        }

        return ret;
    }
}
