pragma solidity >=0.5.16;


contract message {
      
    string public lemessage;
 
    constructor() public {
    }
      
    function definirMessage(string memory _nouveaumessage,address payable _address) public payable{
       uint frais = 0.01 ether;
       require(msg.value == frais);
       address payable monPortefeuille = _address;
       monPortefeuille.transfer(msg.value);
       lemessage = _nouveaumessage;
    }
      
    function voirMessage() public view returns (string memory){  
        return lemessage;
    }
    function gb(address _a) public view returns(uint){
        return (_a.balance);
    }
}
