interface IERC20 {
    function totalSupply() external view returns (uint);
    function balanceOf(address account) external view returns (uint);
    function transfer(address recipient, uint amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint);
    function approve(address spender, uint amount) external returns (bool);
    function transferFrom(
        address sender,
        address recipient,
        uint amount
    ) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);
}

contract washington {

    address private constant UNISWAP_V2_ROUTER = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    address private constant UNISWAP_V3_ROUTER = 0xE592427A0AEce92De3Edee1F18E0157C05861564;
    address private owner;

    event Swap(address indexed from, uint amountOut);
    event Swap2(address indexed from, uint[] amountFinal);

    constructor(){
        owner = msg.sender;
    }

    function swap(address _tokenIn, address _tokenOut, uint256 _amountIn, uint24 fee, uint256 _deadline, uint256 _amountOutMinV3, uint256 _amountOutMinV2) external payable
    {
        require(msg.sender==owner);
        address[] memory path;
        path = new address[](2);
        path[0] = _tokenIn;
        path[1] = _tokenOut;

        uint160 sqrtPriceLimitX96 = 0;

        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams(
            _tokenIn,
            _tokenOut,
            fee,
            address(this),
            _deadline,
            _amountIn,
            _amountOutMinV3,
            sqrtPriceLimitX96
        );

        uint256 amount = ISwapRouter(UNISWAP_V3_ROUTER).exactInputSingle{value: msg.value}(params);

        path[0]=_tokenOut;
        path[1]=_tokenIn;
        IERC20(_tokenOut).approve(UNISWAP_V2_ROUTER,amount);
        uint[] memory amountFinal = IUniswapV2Router02(UNISWAP_V2_ROUTER).swapExactTokensForETH(amount,_amountOutMinV2,path,msg.sender,_deadline);
        emit Swap2(msg.sender,amountFinal);
    }

    function swap2(address _tokenIn, address _tokenOut, uint256 _amountIn, uint24 fee, uint256 _deadline, uint256 _amountOutMinV3, uint256 _amountOutMinV2) external payable{

        require(msg.sender==owner);
        address[] memory path;
        path = new address[](2);
        path[0]=_tokenIn;
        path[1]=_tokenOut;
        uint[] memory amountOut = IUniswapV2Router02(UNISWAP_V2_ROUTER).swapExactETHForTokens{value: msg.value}(_amountOutMinV2,path,address(this),_deadline);

        IERC20(_tokenOut).approve(UNISWAP_V3_ROUTER, amountOut[amountOut.length-1]);

        uint160 sqrtPriceLimitX96 = 0;

        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams(
            _tokenOut,
            _tokenIn,
            fee,
            msg.sender,
            _deadline,
            amountOut[amountOut.length-1],
            _amountOutMinV3,
            sqrtPriceLimitX96
        );

        uint out = ISwapRouter(UNISWAP_V3_ROUTER).exactInputSingle(params);
        emit Swap(msg.sender,out);
    }

    function withdrawETH(uint256 _amount) public{
        require(msg.sender==owner);
        msg.sender.transfer(_amount);
    }

    function withdrawERC(address _token, uint256 _amount) public{
        require(msg.sender==owner);
        IERC20(_token).transfer(msg.sender,_amount);
    }

    fallback() external payable{}
}

