contract Test {
    
    uint256 public m_Launched;
    uint256 public calledAt;

    constructor() {

    }

    function launch(uint8 _timer) external {
        m_Launched = block.timestamp + _timer;
    }

    function callMe() external {
        calledAt = block.timestamp;
    }
}
