pragma solidity 0.6.6;

interface SwapInterface {
    function ETHtoUSD() external view returns(uint256);
}
contract EthSwapTest {
    SwapInterface public swapSourceAddress = SwapInterface(0xc4888352C06799aC450d9e24563ef1D85CeDe3f6);
    function eth_rate() external view returns(uint256) {
        return swapSourceAddress.ETHtoUSD();
    }
}
