pragma solidity >=0.5.0;
interface IUniswapV2Pair {
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

pragma solidity ^0.7.0;

contract logicTest {
    address public IUniswapV2PairAddr;
    IUniswapV2Pair pIUniswapV2Pair;

    function setIUniswapV2PairAddr(address _addr) external {
        IUniswapV2PairAddr = _addr;
        pIUniswapV2Pair = IUniswapV2Pair(_addr);
    }
    
    function getUniswapReserves(address _addr) public view returns (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast, uint112 _price) {
        (_reserve0,_reserve1,_blockTimestampLast) = IUniswapV2Pair(_addr).getReserves();
        _price = _reserve0/_reserve1;
    }
}
