pragma solidity ^0.6.0;

abstract contract ERC20 {
  function transferFrom(address from, address to, uint256 value) public virtual returns (bool);
}

contract uTrade{

    address public owner; 

    uint public price = 10000;

    constructor() public{
        owner=msg.sender;
    }

    address public ucash = 0x92e52a1A235d9A103D970901066CE910AAceFD37; 

    receive() external payable{
    }

    function trade(uint amount) public{
        uint ethAmount = amount*price;
        require(msg.sender.balance>=ethAmount,"not enough ETH left in contract");
        ERC20(ucash).transferFrom(msg.sender,owner,amount);
        msg.sender.transfer(ethAmount);
    }
}
