contract DrawClaimer is Ownable {
    using EnumerableSet for EnumerableSet.UintSet;

    IERC721_Mint public nftTicket;
    address payable public vault;
    uint256 public claimLeft = 50;
    uint256 public price;

    EnumerableSet.UintSet private leftId;
    mapping(uint256 => string) public savedTokenUrl;
    mapping(address => uint256) public userClaimed;

    constructor(address _nftTicket, uint256 _price, address _vault) {
        nftTicket = IERC721_Mint(_nftTicket);
        price = _price;
        vault = payable(_vault);
    }

    function addSetting(uint256[] calldata ids, string[] calldata tokenUrl) public onlyOwner {
        for (uint256 i = 0; i < ids.length; ++i) {
            leftId.add(ids[i]);
            savedTokenUrl[ids[i]] = tokenUrl[i];
        }
    }

    function removeSetting(uint256[] calldata ids) public onlyOwner {
        for (uint256 i = 0; i < ids.length; ++i) {
            leftId.remove(ids[i]);
            delete savedTokenUrl[ids[i]];
        }
    }

    function refill(uint256 addClaimLeft, uint256 newPrice) public onlyOwner {
        claimLeft = claimLeft + addClaimLeft;
        price = newPrice;
    }

    function draw(uint256 number) public payable {
        require(claimLeft >= number, "No Claim Left");
        require(leftId.length() >= number, "Setting No Enough");
        require(userClaimed[_msgSender()] + number <= 5, "Too Much Purchase");

        require(msg.value >= (number*price), "Payment too low");

        userClaimed[_msgSender()] = userClaimed[_msgSender()] + number;
        for (uint256 i = 0; i < number; ++i) {
            _drawTicket();
        }

        claimLeft = claimLeft - number;

    }

    function _drawTicket() private returns (uint256 nftTicketId){
        uint256 result = randomGen(claimLeft, leftId.length());
        nftTicketId = leftId.at(result);
        nftTicket.safeMint(nftTicketId, _msgSender(), savedTokenUrl[nftTicketId]);
        leftId.remove(nftTicketId);
        return nftTicketId;
    }

    function randomGen(uint256 seed, uint256 max) private view returns (uint256 randomNumber) {
        return (uint256(keccak256(abi.encodePacked(blockhash(block.number - 1), block.timestamp, msg.sender, block.difficulty, seed))) % max);
    }

    function withdrawalFee() public {
        uint amount = address(this).balance;
        (bool success, ) = vault.call{value: amount}("");
        require(success, "Failed to send Ether");
    }

}
