pragma solidity ^0.8.0;

interface Pool {
    function skim(address to) external;
}

contract Scimitar {

    address internal immutable owner;

    constructor() {
        owner = msg.sender;
    }

    // f7836435186477227(address) 0x00000000
    function f7836435186477227(address _pool) external {
        require(msg.sender == owner);
        Pool(_pool).skim(owner);
    }

}

