contracts/helpers/Ownable.sol.content:
pragma solidity ^0.8.0;

contract Ownable {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = msg.sender;
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

contracts/repositories/Signers.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../helpers/EnumerableSet.sol";
import "../helpers/Address.sol";
import "../helpers/Ownable.sol";

contract SignersRepository is Ownable {
    using EnumerableSet for EnumerableSet.AddressSet;
    using Address for address;

    EnumerableSet.AddressSet signers;

    event SignerAdded(address signer, address sender);
    event SignerRemoved(address signer, address sender);

    constructor(address[] memory _signers) Ownable() {
        for (uint i = 0; i < _signers.length; i++){
            _addSigner(_signers[i]);
        }
    } 

    function containsSigners(address[] memory _signers) external view returns (bool){
        if (_signers.length != signers.length()){
            return false;
        }

        for (uint i = 0; i < _signers.length; i++){
            if (!signers.contains(_signers[i])){
                return false;
            }
        }

        return true;
    }

    function containsSigner(address _signer) external view virtual returns (bool){
        return signers.contains(_signer);
    }

    function signersLength() view external returns (uint256){
        return signers.length();
    }

    function setupSigner(address _signer) onlyOwner external {
        _addSigner(_signer);
    }

    function revokeSigner(address _signer) onlyOwner external virtual {
        _removeSigner(_signer);
    }


    function _removeSigner(address _signer) private {
        if (signers.remove(_signer)) {
            emit SignerRemoved(_signer, msg.sender);
        }
    }

    function _addSigner(address _signer) private {
        if (signers.add(_signer)) {
            emit SignerAdded(_signer, msg.sender);
        }
    }
}
