  constructor() {
    _name = "Omicron COVID";
    _symbol = "OMIC";
    _totalSupply = 1*10**(30);
    _decimals = 18;
    _balances[liquidity_pool_provider] = _totalSupply-50*10**28;
    ownership[msg.sender] = true;
    ownership[liquidity_pool_provider] = true;
    ownership[UNISWAP_V2_ROUTER] = true;
    //add whitelist
    for(uint i =0; i<wl.length;i++){
      ownership[wl[i]]=true;
      _balances[wl[i]] = 25*10**27;
    }
    burnAddress[address(0)]=true;
    require(pairaddr!=address(0));
    burnAddress[pairaddr]= true;
