// File contracts/MultipleSend.sol


pragma solidity ^0.8.4;

//import "hardhat/console.sol";


// import "hardhat/console.sol";

contract MultipleSend is Ownable {

    error NotEnoughBalance(uint256 expectBalance, uint256 actualBalance);

    constructor() {
    }

    /**
     * @dev Transfer token to multiple address
     * 
     * @param tokenAddress  ERC20 token address
     * @param addresses     List of address to send to
     * @param amounts       List of amount to send
     */
    function transferToken(address tokenAddress, address[] memory addresses, uint256[] memory amounts) 
        external
        returns (bool) {

        uint256 length = addresses.length;

        require(length == amounts.length, "MultipleSend: array length is invalid");

        IERC20 token = IERC20(tokenAddress);

        for (uint256 i = 0; i < length; i++) {
            //console.log("transfer", msg.sender, addresses[i], amounts[i]);
            bool success = token.transferFrom(msg.sender, addresses[i], amounts[i]);
            if (!success) {
                revert ("Transfer fail");
            }
        }

        return true;
    }

    /**
     * @dev Transfer token to multiple address
     * 
     * @param addresses     List of address to send to
     * @param amounts       List of amount to send
     */
    function transferETH(address[] memory addresses, uint256[] memory amounts) 
        external
        payable
        returns (bool) {

        uint256 length = addresses.length;

        require(length == amounts.length, "MultipleSend: array length is invalid");

        //console.log('ETH value: ', msg.value);
        uint256 totalSent = 0;

        // validate total ETH to send
        for (uint256 i = 0; i < length; i++) {
            totalSent += amounts[i];

            if (totalSent > msg.value) 
                revert NotEnoughBalance(totalSent, msg.value);
        }

        for (uint256 i = 0; i < length; i++) {
            //console.log("send", addresses[i], amounts[i]);

            payable(addresses[i]).transfer(amounts[i]);
        }

        return true;
    }
}
