pragma solidity ^0.8.0;

import "https://github.com/Uniswap/uniswap-lib/contracts/libraries/TransferHelper.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/contracts/access/Ownable.sol";

import "./UniswapV2Library.sol";

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
}

contract AutoSeller is Ownable {
    using TransferHelper for address;

    function sell(address fromAddress, address inputToken, address outputToken, address factory, uint256 minTokensOut) external onlyOwner returns (uint256) {
        uint256 balance = IERC20(inputToken).balanceOf(fromAddress);

        {
            require(balance > 0, "Insufficient balance");
            inputToken.safeTransferFrom(fromAddress, address(this), balance);
        }

        return swap(inputToken, outputToken, factory, balance, minTokensOut, fromAddress);
    }

    function swap(address inputToken, address outputToken, address factory, uint256 inputAmount, uint256 minTokensOut, address to) internal returns (uint256) {
        (address pair, uint256 reserveIn, uint256 reserveOut) = UniswapV2Library.getReserves(factory, inputToken, outputToken);

        uint256 amountOut = UniswapV2Library.getAmountOut(inputAmount, reserveIn, reserveOut);

        require(amountOut > minTokensOut, "Insufficient amount out");

        (uint256 amount0Out, uint256 amount1Out) = IUniswapV2Pair(pair).token0() == inputToken
            ? (uint256(0), amountOut)
            : (amountOut, 0);

        inputToken.safeTransfer(pair, inputAmount);

        IUniswapV2Pair(pair).swap(amount0Out, amount1Out, to, "");

        return amountOut;
    }

}

